    36→    $.ajax({
    37→        url: '/preguntas-ofertas-laborales',
    38→        type: 'GET',
    39→        data: {
    40→            vacante_id: vacante_id,
    41→            etapa_vacante_id: etapa_vacante_id_seleccionado
    42→        },
    43→        success: function(respuesta) {
    44→            preguntas_prefiltro_guardadas = respuesta.data || [];
    45→            pintarPreguntasPrefiltro();
    46→        },
    47→        error: function(error) {
    48→            console.error('Error al cargar preguntas de prefiltro:', error);
    49→            preguntas_prefiltro_guardadas = [];
    50→        }
    51→    });
