    54→/**
    55→ * Pintar las preguntas guardadas en el contenedor
    56→ *
    57→ * @returns {void}
    58→ * @author Andres Morales
    59→ */
    60→function pintarPreguntasPrefiltro() {
    61→    var contenedor = $('#contenedor-preguntas-movimiento-4');
    62→    contenedor.empty();
    63→
    64→    if (preguntas_prefiltro_guardadas.length === 0) {
    65→        return;
    66→    }
    67→
    68→    preguntas_prefiltro_guardadas.forEach(function(pregunta) {
    69→        contador_preguntas_prefiltro++;
    70→
    71→        var tipo_pregunta = pregunta.es_abierta ? 'abierta' : (pregunta.opciones && pregunta.opciones.length > 0 ? 'múltiple' : 'única');
    72→
    73→        if (!pregunta.es_abierta && pregunta.opciones) {
    74→            var respuestas_correctas = pregunta.opciones.filter(function(opcion) {
    75→                return opcion.es_correcta;
    76→            });
    77→            tipo_pregunta = respuestas_correctas.length > 1 ? 'múltiple' : 'única';
    78→        }
    79→
    80→        var pregunta_html = '';
    81→
    82→        if (tipo_pregunta === 'abierta') {
    83→            pregunta_html = generarHTMLPreguntaAbierta(pregunta, contador_preguntas_prefiltro);
    84→        } else {
    85→            pregunta_html = generarHTMLPreguntaSeleccion(pregunta, contador_preguntas_prefiltro, tipo_pregunta);
    86→        }
    87→
    88→        contenedor.append(pregunta_html);
    89→    });
