/**
 * Convertir fecha de formato YYYY-MM-DD a DD-MM-YYYY.
 *
 * @param {string} fecha Fecha en formato ISO (YYYY-MM-DD).
 * @returns {string} Fecha convertida a formato DD-MM-YYYY.
 * @author Andres Morales
 * @author Sergio Vargas
 */
const convertirFecha = fecha => {
    if (!fecha) return '';
    fecha = String(fecha).substring(0, 10);
    return fecha.includes('-') ? fecha.split('-').reverse().join('-') : 'Formato incorrecto';
};

/**
 * Formatear fecha a formato dd/mm/yyyy - hh:mm AM/PM.  Ejemplo: 01/Feb/2022 - 12:00 PM
 *
 * @param {string} fecha_entrada fecha en formato ISO
 * @returns {string} fecha y hora formateada
 * @author Andres Morales
 */
function formatearFecha(fecha_entrada) {
    var fecha = new Date(fecha_entrada);
    var dia = fecha.getDate();
    var mes = fecha.toLocaleString('es-ES', { month: 'short' });
    var anio = fecha.getFullYear();
    let hora = fecha.getHours();
    let minuto = fecha.getMinutes();
    let ampm = hora >= 12 ? 'PM' : 'AM';
    hora = hora % 12 || 12;
    minuto = minuto.toString().padStart(2, '0');
    return `${dia}/${mes}/${anio} - ${hora}:${minuto} ${ampm}`;
}

function formatearFecha2(fecha_entrada) {
    let partes = fecha_entrada.split(' ');
    let fecha = partes[0].split('-');
    let hora = partes[1];
    let fechaISO = `${fecha[2]}-${fecha[1]}-${fecha[0]}T${hora}`;

    let date = new Date(fechaISO);

    if (isNaN(date.getTime())) {
        return 'Fecha inválida';
    }

    let dia = date.getDate();
    let mes = date.toLocaleString('eu-EU', { month: 'short' });
    let anio = date.getFullYear();
    return `${dia}/${mes}/${anio}`;
}

/**
