function contraerMenu() {
    if ($('.sidebar-menu').hasClass('close')) {
        $('.sidebar-menu').removeClass('d-none');
        localStorage.setItem('opcion_menu_storage', 'abierto');
        $('#btn-collapse').attr('data-bs-original-title', 'Contraer');
        $('#btn-collapse').attr('data-bs-title', 'Contraer');
        $(".menu-bar").each(function() {
            $(this).removeClass("px-2").addClass("px-5");
        });
        $('[data-bs-toggle="tooltip"]').each(function () { new bootstrap.Tooltip(this); });
        $('.lista-primary[data-bs-toggle="tooltip"]').each(function () {
            var tooltip_instancia = bootstrap.Tooltip.getInstance(this);
            if (tooltip_instancia) {
                tooltip_instancia.dispose();
            }
        });
    } else {
        $('.sidebar-menu').addClass('d-none');
        localStorage.setItem('opcion_menu_storage', 'cerrado');
        $('#btn-collapse').attr('data-bs-original-title', 'Expandir');
        $('#btn-collapse').attr('data-bs-title', 'Expandir');
