 * @author Andres Morales
 */
function mostrarMenuStorage() {
    $(document).ready(function() {
        $('.lista-primary[data-bs-toggle="tooltip"]').each(function () {
            var tooltip_instancia = bootstrap.Tooltip.getInstance(this);
            if (tooltip_instancia) {
                tooltip_instancia.dispose();
            }
        });

        if (window.innerWidth < 768) {
            localStorage.setItem('opcion_menu_storage', 'cerrado');
