/**
 * Enviar la solucitud de suplantación de rol y empresa al Back-end.
 *
 * @param {function|null} callback_exito Callback ejecutado en caso de éxito.
 * @param {string} url URL de suplantación.
 * @param {string} _token Token CSRF del formulario.
 * @param {number} empresa_id Id de la empresa a suplantar.
 * @param {number|null} rol_id Id del rol a suplantar.
 * @param {function|null} callback_error Callback ejecutado en caso de error.
 * @returns {void}
 * @author Fahibram Cárcamo
 */
async function enviarOrdenSuplantacionRolEmpresa (callback_exito, url, _token, empresa_id = null, rol_id = null, callback_error = null) {

    let condicion_suplantacion = rol_id === null
        ? empresa_id === null
            ? true
            : parseInt($('#empresa_sesion_id').val()) !== parseInt(empresa_id)
        : parseInt($('#rol_sesion_id').val()) !== parseInt(rol_id) && parseInt($('#empresa_sesion_id').val()) !== parseInt(empresa_id);

    if (condicion_suplantacion) {
        let data = { _token };
        if (empresa_id !== null) data.empresa_id = empresa_id;
        if (rol_id !== null) data.rol_id = rol_id;

        await $.ajax({ type: 'POST', url, data })
            .then((respuesta, status, xhr) => {
                if (xhr.status >= 200 && xhr.status < 300 && callback_exito !== null) callback_exito();
            })
            .catch(error => {
                let swal_titulo, swal_texto;

                if (error.status === 404) {
                    if (error.responseJSON.mensaje) {
                        swal_titulo = 'Algo salió mal';
                        if (error.responseJSON.mensaje === 'no existe rol_suplantar') {
                            swal_texto = 'El rol no existe.';
                        } else if (error.responseJSON.mensaje === 'no existe empresa_suplantar') {
                            swal_texto = 'La empresa no existe.';
                        } else if (error.responseJSON.mensaje === 'no existe usuario_suplantar') {
                            swal_texto = 'El usuario no existe.';
                        } else {
                            swal_texto = '';
                        }
                    } else if (error.responseJSON.empresa && error.responseJSON.rol) {
                        swal_titulo = 'No fué posible cambiar de empresa';
                        swal_texto = error.responseJSON.rol === 'Administrador general' || error.responseJSON.rol === 'Propietario'
                            ? `La empresa ${error.responseJSON.empresa} no tiene ningún usuario con el rol Administrador general o Propietario.`
                            : `La empresa ${error.responseJSON.empresa} no tiene ningún usuario con el rol ${error.responseJSON.rol}.`;
                    } else {
                        swal_titulo = 'Algo salió mal';
                        swal_texto = error.responseJSON.errors;
                    }
                } else {
                    swal_titulo = 'Algo salió mal';
                    swal_texto = '';
                }

                if (callback_error !== null) callback_error();

                Swal.fire({
                    title: swal_titulo,
                    text: swal_texto,
                    icon: "error",
                    confirmButtonText: "Aceptar"
                });
            });
    }
}

