        html: `
            <div class="row w-100 mx-0 mt-2">
                <div class="col-12 fw-semibold text-start text-gray-10 text-headline-4">Adquiere esta funcionalidad</div>
                <div class="col-12 mt-5 text-start text-body-2 fw-normal">No tienes esta funcionalidad activa. Por favor, contacta a tu asesor.</div>
                <div class="col-6 mt-5 mb-2">
                    ${crearBoton({
                        clases: 'btn-outline-primary w-100 justify-content-center',
                        texto: 'Cancelar',
                        onclick: `Swal.clickDeny();`
                    })}
                </div>
                <div class="col-6 mt-5 mb-2">
                    ${crearBoton({
                        clases: 'btn-primary w-100 justify-content-center',
                        texto: 'Entiendo',
                        onclick: `Swal.clickConfirm();`
                    })}
                </div>
            </div>
        `,
    }).then(async (resultado) => {
        //
    });
}

/**
 * Busca una funcionalidad por su llave.
 *
 * @param {string} llave Llave de la funcionalidad a buscar.
 * @param {Array|null} funcionalidades funcionalidades.
 * @returns {boolean}
 * @author Andres Gonzalez
 */
function buscarFuncionalidad(llave, funcionalidades = null) {
    let datos = funcionalidades ?? funcionalidades_disponibles_empresa;

    if (!Array.isArray(datos) || datos.length === 0) {
        return false;
    }

    return datos.some(item => item?.funcionalidad?.llave === llave);
}

