// Mensajes en español para el plugin Select2
const mensajes_select2 = {
    'noResults'              : () => { return 'No hay resultados para la búsqueda.'; },
    'searching'              : () => { return 'Buscando...'; },
    'Loading'                : () => { return 'Cargando...'; },
    'loadingMore'            : () => { return 'Cargando más resultados...'; },
    'dropdownFooter'         : (texto) => { return `Mostrando ${texto} resultados`; },
    'emptyOptions'           : () => { return 'No hay opciones disponibles.'; },
    'appendItems'            : (items) => { return `Agregando ${items.length} elementos.`; },
    'maximumSelectionReached': () => { return 'Se ha alcanzado el número máximo de selecciones.'; },
    'selectOnClose'          : () => { return 'Seleccione una opción al cerrar el menú.'; },
    'escapeKeyUp'            : () => { return 'Presione \'Escape\' para cerrar el menú.'; },
    'errorLoading'           : () => { return 'Error al cargar datos. Inténtalo de nuevo más tarde.'; },
    'inputTooShort'          : (elemento) => { return `Ingresa al menos ${elemento.minimum} caracteres para iniciar una búsqueda.`; },
    'noMatches'              : () => { return 'No se encontraron coincidencias para tu búsqueda.'; },
    'allowClear'             : () => { return 'Borrar selección'; },
    'SelectionTooLong'       : (conteo, maximo) => { return `Solo puedes seleccionar ${maximo} opciones.`; },
    'loadMore'               : () => { return 'Cargando más resultados...'; },
    'removing'               : (seleccion) => { return 'Eliminando...'; },
    'matches'                : (coincidencias, total) => {
        if (coincidencias === 0) {
            return 'No hay coincidencias.';
        } else if (coincidencias === 1) {
            return 'Se encontró 1 coincidencia.';
        } else {
            return `Se encontraron ${coincidencias} coincidencias entre ${total}.`;
        }
    }
};

