/**
 * Definir un campo destinado a recibir un número de teléfono celular.
 *
 * Esta función debe llamarse en el evento ready del objeto global document:
 * `$(document).ready(...)`. Ademas debe incluirse un pequeño fragmento de código en la función
 * `validacionAdicional` para que se agregué el código del número de celular al valor de campo
 * cuando se envíe el formulario donde se encutra el campo.
 *
 * ```
 * if (
 *   $('#input_celular_id').length &&
 *   $('#input_celular_id').val() !== '' &&
 *   input_celular.getNumber() !== ''
 * ) {
 *   $('#input_celular_id').val(input_celular.getNumber().slice(1));
 * }
 * ```
 *
 * Donde `input_celular` es el objeto retornado por esta función.
 *
 * @param {string} input_celular_id Id del campo.
 * @param {string} nombre_input_celular Nombre del campo.
 * @param {string} placeholder_input_celular Placeholder del campo.
 * @param {boolean} obligatorio El campo es obligatorio en el formulario.
 * @returns {object}
 * @author Fahibram Cárcamo
 */
function definirInputTipoCelular(input_celular_id, nombre_input_celular, placeholder_input_celular, obligatorio = true) {
    /**
     * 🌎 Campo para ingresar un número de telefono con selectór de código de país con banderas.
     * Este componente se creó con la librería International Telephone Input
     * (https://intl-tel-input.com).
     *
     * La linea hiddenInput: () => ({ phone: 'celular_contacto' }) tiene como función establecer el
     * nombre del campo, es decir, la llave del par llave-valor que se adjuntará en los datos del
     * formulario cuando se envíe, en este caso 'celular_contacto'.
     *
     * Las lienas autoPlaceholder: 'aggressive' y
     * customPlaceholder: (placeholder, country) => 'Celular*' se usan para definir el placeholder
     * del campo.
     *
     * Con $('#celular_contacto').attr('required', true); se establece el campo como requerido
     * dentro del formulario, se hace de esta forma porque la librería no admite el atributo
     * required directamente en el componente html.
     *
     * @author Fahibram Cárcamo
     */
    if (document.querySelector(`#${input_celular_id}`)) {
        const dropdown_dinamico = document.createElement('div');
        document.body.appendChild(dropdown_dinamico);

        let campo_celular = window.intlTelInput(document.querySelector(`#${input_celular_id}`), {
            nationalMode: true,
            initialCountry: 'co',
            separateDialCode: true,
            autoInsertDialCode: false,
            autoPlaceholder: 'aggressive',
            dropdownContainer: dropdown_dinamico,
            customPlaceholder: (placeholder, country) => placeholder_input_celular,
            loadUtils: () => import('https://cdn.jsdelivr.net/npm/intl-tel-input@25.4.2/build/js/utils.js'),
            onlyCountries: [
                'CO', // Colombia
                'EC', // Ecuador
                'AR', // Argentina
                'CL', // Chile
                'VE', // Venezuela
                'PE', // Perú
                'BO', // Bolivia
                'UY', // Uruguay
                'PY', // Paraguay
                'PA', // Panamá
                'MX', // México
                'CR', // Costa Rica
                // 'PR', // Puerto Rico
                'CU', // Cuba
                // 'DO', // República Dominicana
                'SV', // El Salvador
                'GT', // Guatemala
                'HN', // Honduras
                'NI', // Nicaragua
                'ES', // España
                'US', // Estados Unidos
                'GQ'  // Guinea Ecuatorial
            ]
        });

        if (obligatorio) $(`#${input_celular_id}`).attr('required', true);

        return campo_celular;
    }

    return null;
}

/**
 * Establece la validación adicional que se aplica a un campo de tipo celular antes de enviar sus
 * datos en un formulario.
 *
 * @param {HTMLInputElement} input Elemento HTML del campo de tipo celular.
 * @param {string} input_id Id del elemento HTML del campo de tipo celular.
 * @author Fahibram Cárcamo
 * @author Jair Carvajal
 */
function validacionAdicionalInputTipoCelular(input, input_id) {
    let numero_completo = input.getNumber();
    let indicativo = '+' + input.getSelectedCountryData().dialCode;
    let codigo_iso2_pais = input.getSelectedCountryData().iso2;
    let numero_sin_indicativo = numero_completo.replace(indicativo, '');
    $(`#indicativo_${input_id}`).val(indicativo);
    $(`#codigo_iso2_pais_${input_id}`).val(codigo_iso2_pais);
    $(`#${input_id}`).val(numero_sin_indicativo);
}

/**
 * Rellenar un input de tipo celular.
 *
 * @param {string} indicativo_celular Código del país del número de celular.
 * @param {string} codigo_pais_iso2 Código ISO2 del país del número de celular.
 * @param {string} numero_celular Número de celular sin código de país.
 * @param {string} input_celular_id ID del input de tipo celular.
 * @param {HTMLInputElement} input_celular Input de tipo celular.
 * @returns {void}
 * @author Daniel Beltrán
 * @author Fahibram Cárcamo
 */
function rellenarInputCelular(indicativo_celular, codigo_pais_iso2, numero_celular, input_celular_id, input_celular) {
    $(`#${input_celular_id}`).val(numero_celular);
    if (![null, undefined].includes(codigo_pais_iso2)) input_celular.setCountry(codigo_pais_iso2);
}

/**
 * Convierte imágenes de base64 a Blob.
 *
 * @param {string} cadena_base_64 Base 64 de la imagen.
 * @returns {Promise<Array>}
 * @author Andres Gonzalez
 */
function imagenBase64Blob(cadena_base_64) {
    if (!cadena_base_64 || !cadena_base_64.startsWith('data:image')) return null;
    let datos_base_64 = cadena_base_64.split(',')[1];
    let tipo_mime = cadena_base_64.match(/data:(.*?);base64/)[1];
    let caracteres_binarios = atob(datos_base_64);
    let arreglo_bytes = new Array(caracteres_binarios.length).fill(0).map((_, i) => caracteres_binarios.charCodeAt(i));
    let bytes_uint8 = new Uint8Array(arreglo_bytes);
    return new Blob([bytes_uint8], { type: tipo_mime });
}


/**
 * Limpiar estructuras html innecesarias en el check editor.
 *
 * @param {string} html - Cadena HTML generada por CKEditor a limpiar
 * @returns {string} HTML limpio sin elementos específicos de CKEditor
 * @author Andres Gonzalez
 * @author Jair Carvajal
 */
function limpiarHTMLCKEditor(html) {
    // Remover todos los elementos con clases de CKEditor
    html = html.replace(/<div class="ck[^"]*"[^>]*>.*?<\/div>/gs, '');
    html = html.replace(/<span class="[^"]*ck[^"]*"[^>]*>/gi, '<span>');

    // Limpiar spans de imagen inline
    html = html.replace(/<span class="image-inline[^"]*"[^>]*>/gi, '');
    html = html.replace(/<\/span>/g, '');

    // Remover elementos de upload y widgets
    html = html.replace(/<div class="ck-image-upload[^"]*"[^>]*>.*?<\/div>/gs, '');
    html = html.replace(/<div class="ck-fake-selection[^"]*"[^>]*>.*?<\/div>/gs, '');

    // Remover atributos innecesarios
    html = html.replace(/contenteditable="[^"]*"/gi, '');
    html = html.replace(/data-cke-filler="[^"]*"/gi, '');

    // Limpiar párrafos vacíos con br
    html = html.replace(/<p><br[^>]*><\/p>/gi, '');
    html = html.replace(/<br data-cke[^>]*>/gi, '');

    // Limpiar espacios múltiples y saltos de línea
    html = html.replace(/\s+/g, ' ');
    html = html.replace(/>\s+</g, '><');

    return html.trim();
}

/**
 * Ordena estudios por fecha_inicio más reciente (de más nueva a más antigua).
 * Preserva los índices originales para permitir edición y eliminación correcta.
 *
 * @param {Array} estudios - Array de objetos con estudios
 * @returns {Array} Array de estudios ordenados con propiedad indice_original
 * @author Jair Carvajal
 */
function ordenarEstudiosPorFecha(estudios) {
    // Mapear con índice original
    let estudios_con_indice = estudios.map((estudio, indice_original) => ({
        ...estudio,
        indice_original: indice_original
    }));

    // Ordenar estudios por fecha_inicio (más reciente primero)
    let estudios_ordenados = estudios_con_indice.sort((a, b) => {
        // Si ambos tienen fecha_inicio, ordenar por más reciente
        if (a.fecha_inicio && b.fecha_inicio) {
            return moment(b.fecha_inicio).diff(moment(a.fecha_inicio));
        }
        // Si solo uno tiene fecha_inicio, ese va primero
        if (a.fecha_inicio && !b.fecha_inicio) return -1;
        if (!a.fecha_inicio && b.fecha_inicio) return 1;
        // Si ninguno tiene fecha_inicio, mantener orden original
        return 0;
    });

    return estudios_ordenados;
}

/**
 * Ordena experiencias laborales por fecha: primero las que está trabajando actualmente (sin fecha_fin),
 * luego por fecha_fin más reciente.
 * Preserva los índices originales para permitir edición y eliminación correcta.
 *
 * @param {Array} experiencias - Array de objetos con experiencias laborales
 * @returns {Array} Array de experiencias ordenadas con propiedad indice_original
 * @author Jair Carvajal
 */
function ordenarExperienciasPorFecha(experiencias) {
    // Mapear con índice original
    let experiencias_con_indice = experiencias.map((experiencia, indice_original) => ({
        ...experiencia,
        indice_original: indice_original
    }));

    // Ordenar experiencias
    let experiencias_ordenadas = experiencias_con_indice.sort((a, b) => {
        // Si 'a' no tiene fecha_fin (trabaja actualmente), va primero
        if (!a.fecha_fin && b.fecha_fin) return -1;
        // Si 'b' no tiene fecha_fin (trabaja actualmente), va primero
        if (a.fecha_fin && !b.fecha_fin) return 1;
        // Si ambos están trabajando actualmente (sin fecha_fin), mantener el orden actual
        if (!a.fecha_fin && !b.fecha_fin) return 0;
        // Si ambos tienen fecha_fin, ordenar por fecha más reciente primero (descendente)
        return moment(b.fecha_fin).diff(moment(a.fecha_fin));
    });

    return experiencias_ordenadas;
}

/**
 * Obtener créditos disponibles de la empresa y actualizar el tag.
 * - Si tiene créditos >= 20% del plan: tag azul
 * - Si tiene créditos < 20% del plan: tag amarillo
 * - Si no tiene créditos: tag gris "Sin créditos"
 *
 * @param {number} empresa_id ID de la empresa
 * @param {string} contenedor_id ID del contenedor donde se mostrará el tag
 * @param {boolean} verificar_modal Si se debe verificar si mostrar el modal de advertencia
 * @returns {void}
 * @author Andres Gonzalez
 * @author Daniel Beltrán
 */
function obtenerCreditosDisponiblesEmpresa(empresa_id, contenedor_id = 'contenedor-tag-creditos', verificar_modal = false) {
    if (!empresa_id || !$(`#${contenedor_id}`).length) {
        return;
    }

    $.ajax({
        url: `/empresas/${empresa_id}/creditos`,
        method: 'GET',
        dataType: 'json',
        success: function (respuesta) {
            if (respuesta?.data) {
                let creditos_disponibles = respuesta?.data?.creditos_disponibles ?? 0;
                let creditos_plan = respuesta?.data?.creditos_plan ?? 0;
                let plan_credito = respuesta?.data?.plan_credito;
                let porcentaje_disponible = creditos_plan > 0 ? (creditos_disponibles / creditos_plan) * 100 : 0;

                let texto_tag = '';
                let clase_tag = '';
                let icono_final = '';

                if (creditos_disponibles <= 0 || plan_credito === null) {
                    texto_tag = 'Sin créditos';
                    clase_tag = 'tag-tonal-gray';
                    icono_final = `<i class="bi bi-info-circle ms-1" role="button" tabindex="0" ${crearTooltip({ placement: 'right', content: 'No tienes créditos disponibles en este momento. Renueva tu plan o adquiere más créditos para continuar.' })}></i>`;
                } else if (porcentaje_disponible < 20) {
                    texto_tag = `Créditos disponibles: ${formatearNumero(creditos_disponibles)}`;
                    clase_tag = 'tag-filled-yellow';
                    icono_final = `<i class="bi bi-info-circle ms-1" role="button" tabindex="0" ${crearTooltip({ placement: 'right', content: 'Estás cerca de agotar tus créditos. Te queda menos del 20% del total disponible. Considera recargar o revisar tu consumo para evitar interrupciones.' })}></i>`;
                } else {
                    texto_tag = `Créditos disponibles: ${formatearNumero(creditos_disponibles)}`;
                    clase_tag = 'tag-tonal-blue';
                }

                $(`#${contenedor_id}`).html(crearTag({
                    texto: texto_tag,
                    class: clase_tag,
                    icono_final: icono_final,
                }));

                // Verificar si se debe mostrar el modal de advertencia
                if (verificar_modal && plan_credito !== null) {
                    verificarYMostrarModalAdvertenciaCreditosDisponibles(porcentaje_disponible, empresa_id);
                }
            } else {
                $(`#${contenedor_id}`).html(crearTag({
                    texto: 'No tienes plan de créditos',
                    class: 'tag-tonal-gray'
                }));
            }

            inicializarTooltips();
        },
        error: function () {
            $(`#${contenedor_id}`).html(crearTag({
                texto: 'Error al cargar créditos',
                class: 'tag-tonal-gray'
            }));
        }
    });
}

/**
 * Verificar si se debe mostrar el modal de advertencia de créditos disponibles.
 * Los umbrales son: 20%, 10%, 5% y 0%.
 * El modal solo se muestra una vez por día para cada umbral alcanzado.
 * Si baja a un umbral más bajo, se muestra ese nuevo umbral aunque ya se haya mostrado otro ese día.
 *
 * @param {number} porcentaje_disponible Porcentaje de créditos disponibles
 * @param {number} empresa_id ID de la empresa
 * @returns {void}
 * @author Sergio Vargas
 */
function verificarYMostrarModalAdvertenciaCreditosDisponibles(porcentaje_disponible, empresa_id) {
    // Determinar el umbral actual alcanzado (el más bajo que aplica)
    let umbral_actual = null;

    for (let umbral of UMBRALES_ADVERTENCIA_CREDITOS) {
        if (porcentaje_disponible <= umbral) {
            umbral_actual = umbral;
        } else {
            break;
        }
    }

    // Si no se alcanzó ningún umbral, no mostrar el modal
    if (umbral_actual === null) {
        return;
    }

    // Obtener la fecha actual en formato YYYY-MM-DD
    let fecha_hoy = new Date().toISOString().split('T')[0];
    let llave_storage = `modal_creditos_${empresa_id}`;

    // Obtener los datos guardados en localStorage
    let datos_guardados = localStorage.getItem(llave_storage);
    let umbrales_mostrados_hoy = {};

    if (datos_guardados) {
        try {
            let datos = JSON.parse(datos_guardados);
            // Si la fecha guardada es de hoy, usar los umbrales guardados
            if (datos.fecha === fecha_hoy) {
                umbrales_mostrados_hoy = datos.umbrales || {};
            }
            // Si es de otro día, se resetean los umbrales (objeto vacío)
        } catch (e) {
            // Si hay error al parsear, se resetea
            umbrales_mostrados_hoy = {};
        }
    }

    // Verificar si ya se mostró el modal para este umbral hoy
    if (umbrales_mostrados_hoy[umbral_actual]) {
        return;
    }

    // Marcar este umbral como mostrado hoy
    umbrales_mostrados_hoy[umbral_actual] = true;

    // Guardar en localStorage
    localStorage.setItem(llave_storage, JSON.stringify({
        fecha: fecha_hoy,
        umbrales: umbrales_mostrados_hoy
    }));

    // Mostrar el modal
    mostrarModalAdvertenciaCreditosDisponibles(umbral_actual, empresa_id);
}

/**
 * Mostrar el modal de advertencia de créditos disponibles.
 *
 * @param {number} umbral Umbral de porcentaje alcanzado (20, 10, 5 o 0)
 * @param {number} empresa_id ID de la empresa
 * @returns {void}
 * @author Sergio Vargas
 */
function mostrarModalAdvertenciaCreditosDisponibles(umbral, empresa_id) {
    let mensaje_descripcion = umbral === 0
        ? 'Tu saldo de créditos se ha agotado. Solicita más créditos ahora para evitar interrupciones en tus acciones.'
        : `Tu saldo está por debajo del ${umbral}% de créditos disponibles. Solicita más créditos ahora para evitar interrupciones en tus acciones.`;

    Swal.fire({
        showCloseButton: true,
        closeButtonHtml: '<i class="bi bi-x text-headline-4 text-gray-20"></i>',
        showConfirmButton: false,
        showDenyButton: false,
        showCancelButton: false,
        html: `
            <div class="row w-100 mx-0 mt-2">
                <div class="col-12 fw-semibold text-start text-gray-10 text-headline-4">Solicita más créditos</div>
                <div class="col-12 mt-4 text-start text-body-2 fw-normal text-gray-30">${mensaje_descripcion}</div>
                <div class="col-6 mt-5 mb-2">
                    ${crearBoton({
                        clases: 'btn-outline-primary w-100 justify-content-center',
                        texto: 'Cancelar',
                        onclick: `Swal.close();`
                    })}
                </div>
                <div class="col-6 mt-5 mb-2">
                    ${crearBoton({
                        clases: 'btn-primary w-100 justify-content-center',
                        texto: 'Solicitar más créditos',
                        onclick: `solicitarMasCreditosDesdeModal(${empresa_id});`
                    })}
                </div>
            </div>
        `,
    });
}

/**
 * Solicitar más créditos desde el modal de advertencia.
 * Contacta al ejecutivo de la empresa via WhatsApp.
 *
 * @param {number} empresa_id ID de la empresa
 * @returns {void}
 * @author Sergio Vargas
 */
function solicitarMasCreditosDesdeModal(empresa_id) {
    $.ajax({
        url: `/empresas/${empresa_id}`,
        method: 'GET',
        dataType: 'json',
        success: function(respuesta) {
            if (respuesta?.data?.ejecutivo?.telefono) {
                window.open(`https://wa.me/${respuesta.data.ejecutivo.telefono.replace(/\D/g, '')}?text=Hola,%20me%20gustar%C3%ADa%20solicitar%20cr%C3%A9ditos%20adicionales%20para%20mi%20empresa.`, '_blank');
            } else {
                mostrarToast('danger', 'No se encontró un ejecutivo asignado a la empresa. Por favor, comunícate con soporte para recargar créditos.');
            }
            Swal.close();
        },
        error: function() {
            mostrarToast('danger', 'Ocurrió un error al obtener la información del ejecutivo.');
            Swal.close();
        }
    });
}

/**
 * Obtiene las funcionalidades activas del plan vigente de la empresa del usuario actual.
 *
 * @param {number|null} tipo_prueba_id ID del tipo de prueba (opcional).
 * @returns {Promise} Promesa que se resuelve con las funcionalidades activas.
 * @author Andres Gonzalez
 */
function obtenerFuncionalidadesActivasPlan(tipo_prueba_id = null) {
    let parametros = {};

    if (tipo_prueba_id !== null) {
        parametros.tipo_prueba_id = tipo_prueba_id;
    }

    return $.ajax({
        url: '/funcionalidades-activas-plan',
        method: 'GET',
        dataType: 'json',
        data: parametros,
        success: function(respuesta) {
            funcionalidades_disponibles_empresa = respuesta?.data ?? [];
        }
    });
}

/**
 * Muestra Modal para informar que no cuenta con la funcionalidad
 *.
 * @returns {void}
 * @author Andres Gonzalez
 */
function modalFuncionalidadInactiva() {
    Swal.fire({
        showCloseButton: true,
        closeButtonHtml: '<i class="bi bi-x text-headline-4 text-gray-20"></i>',
        showConfirmButton: false,
        showDenyButton: false,
        showCancelButton: false,
        html: `
            <div class="row w-100 mx-0 mt-2">
                <div class="col-12 fw-semibold text-start text-gray-10 text-headline-4">Adquiere esta funcionalidad</div>
                <div class="col-12 mt-5 text-start text-body-2 fw-normal">No tienes esta funcionalidad activa. Por favor, contacta a tu asesor.</div>
                <div class="col-6 mt-5 mb-2">
                    ${crearBoton({
                        clases: 'btn-outline-primary w-100 justify-content-center',
                        texto: 'Cancelar',
                        onclick: `Swal.clickDeny();`
                    })}
                </div>
                <div class="col-6 mt-5 mb-2">
                    ${crearBoton({
                        clases: 'btn-primary w-100 justify-content-center',
                        texto: 'Entiendo',
                        onclick: `Swal.clickConfirm();`
                    })}
                </div>
            </div>
        `,
    }).then(async (resultado) => {
        //
    });
}

/**
 * Busca una funcionalidad por su llave.
 *
 * @param {string} llave Llave de la funcionalidad a buscar.
 * @param {Array|null} funcionalidades funcionalidades.
 * @returns {boolean}
 * @author Andres Gonzalez
 */
function buscarFuncionalidad(llave, funcionalidades = null) {
    let datos = funcionalidades ?? funcionalidades_disponibles_empresa;

    if (!Array.isArray(datos) || datos.length === 0) {
        return false;
    }

    return datos.some(item => item?.funcionalidad?.llave === llave);
}

