function mostrarModalCreditosIA(servicio_ia_id) {
    // Verificar si el usuario optó por no mostrar el modal
    if (localStorage.getItem('no_mostrar_modal_creditos_ia') === 'true') {
        mostro_modal_creditos_ia = true;
        if (funcion_ejecucion_ia) {
            funcion_ejecucion_ia();
            funcion_ejecucion_ia = null;
        }
        mostro_modal_creditos_ia = false;
        return;
    }

    $.ajax({
        url: `/empresas/${$('#empresa_sesion_id').val()}/creditos`,
        method: 'GET',
        dataType: 'json',
        success: function(respuesta_empresa) {
            if (respuesta_empresa?.data?.creditos_disponibles > 0) {
                $.ajax({
                    url: `/servicios-ia/${servicio_ia_id}/creditos`,
                    method: 'GET',
                    dataType: 'json',
                    success: function(respuesta) {
                        if (respuesta?.data?.consume_creditos && respuesta?.data?.consume_creditos <= respuesta_empresa.data.creditos_disponibles) {
                            $('#cantidad-creditos-descontar-modal').text(respuesta.data.consume_creditos);
                            $('#modal-consumo-creditos-ia').modal('show');
                            mostro_modal_creditos_ia = true;
                            modals_abiertos_ia = $('.modal.show').not('#modal-consumo-creditos-ia').not('#modal-generando-ia');
                            $(modals_abiertos_ia).addClass('d-none');
                        } else {
                            mostrarToastSolicitarCreditosIA();
                        }
                    }
                });
            } else {
                mostrarToastSolicitarCreditosIA();
            }
        }
    });
}

/**
 * Mostrar toast para solicitar créditos IA
 *
 * @return void
 * @author José Manuel Jiménez
 */
function mostrarToastSolicitarCreditosIA() {
    $.ajax({
        url: `/empresas/${$('#empresa_sesion_id').val()}`,
        method: 'GET',
        dataType: 'json',
        success: function(respuesta) {
            let adicional_toast = '';
            if (respuesta?.data?.ejecutivo?.telefono) {
                adicional_toast = `<a href="https://wa.me/${respuesta.data.ejecutivo.telefono.replace(/\D/g, '')}?text=Hola,%20me%20gustar%C3%ADa%20solicitar%20cr%C3%A9ditos%20adicionales%20para%20mi%20empresa." target="_blank">
                    <p class="text-label-1 text-white no-visited text-decoration-none mb-0">Solicitar</p>
                </a>`;
            }
            setTimeout(() => {
                $('#modal-generando-ia').modal('hide');
                $('#modal-cargue-ia').modal('hide');
                mostrarToast('danger', 'No cuentas con créditos. Solicita créditos adicionales.', '', adicional_toast);
            }, 300);
        }
    });
}

/**
 * Continuar ejecución IA luego del modal de créditos
 *
 * @return void
 * @author José Manuel Jiménez
 */
function continuarEjecucionIA() {
    // Guardar preferencia si el checkbox está marcado
    if ($('#no_mostrar_modal_creditos_ia').is(':checked')) {
        localStorage.setItem('no_mostrar_modal_creditos_ia', 'true');
    }

    if (funcion_ejecucion_ia) {
        funcion_ejecucion_ia();
        funcion_ejecucion_ia = null;
    }
    mostro_modal_creditos_ia = false;
