     3→namespace App\Http\Services\Planes;
     4→
     5→use App\Traits\SesionTrait;
     6→use Illuminate\Http\Request;
     7→use App\Models\Agendas\Agenda;
     8→use App\Models\Vacantes\Vacante;
     9→use App\Http\Services\BaseService;
    10→use App\Models\Pruebas\TipoPrueba;
    11→use Illuminate\Support\Collection;
    12→use App\Models\Planes\{Consumo,Plan};
    13→use App\Models\OfertasLaborales\OfertaLaboral;
    14→use App\Http\Repositories\Usuarios\UserRepository;
    15→use App\Http\Services\Empresas\EmpresaPlanService;
    16→use App\Http\Repositories\Agendas\AgendaRepository;
    17→use App\Http\Repositories\Planes\ConsumoRepository;
    18→use App\Http\Repositories\Vacantes\VacanteRepository;
    19→use App\Http\Repositories\Pruebas\TipoPruebaRepository;
