     2→
     3→namespace App\Http\Services\IA;
     4→
     5→use Illuminate\Http\Request;
     6→use App\Models\IA\ServicioIA;
     7→use App\Models\Pruebas\TipoPrueba;
     8→use App\Http\Repositories\IA\ServicioIARepository;
     9→use App\Http\Repositories\Empresas\EmpresaPlanRepository;
    10→
    11→class CreditoIAService
    12→{
    13→    /**
    14→     * Constructor.
    15→     *
    16→     * @param ServicioIARepository $servicio_ia_repositorio Repositorio de servicios de IA.
    17→     * @param EmpresaPlanRepository $empresa_plan_repositorio Repositorio de planes de empresas.
    18→     * @return void
    19→     * @author Sergio Vargas
    20→     * @author Daniel Beltrán
    21→     */
    22→    public function __construct(
    23→        private ServicioIARepository $servicio_ia_repositorio,
    24→        private EmpresaPlanRepository $empresa_plan_repositorio
    25→    ) {}
    26→
    27→    /**
    28→     * Validar si hay créditos disponibles para un endpoint.
    29→     *
    30→     * @param ?int $empresa_plan_id ID del plan de empresa.
    31→     * @param string $endpoint
    32→     * @param ?int $empresa_id ID de la empresa.
    33→     * @return array
    34→     * @author Sergio Vargas
    35→     * @author Daniel Beltrán
    36→     */
    37→    public function validarCreditosDisponibles(?int $empresa_plan_id, string $endpoint, ?int $empresa_id = null): array {
    38→        $servicio             = $this->obtenerServicioPorEndpoint($endpoint);
    39→        $creditos_necesarios  = $servicio?->consume_creditos ?? 0;
    40→        $empresa_plan_id      = $this->resolverEmpresaPlanId($empresa_plan_id, $empresa_id);
    41→        $creditos_disponibles = 0;
    42→
    43→        if ($empresa_plan_id) {
    44→            $empresa_plan         = $this->empresa_plan_repositorio->getUno($empresa_plan_id);
    45→            $creditos_disponibles = $empresa_plan?->creditos_restantes ?? 0;
    46→        }
    47→
    48→        $datos_creditos = [
    49→            'tiene_creditos'       => $creditos_disponibles >= $creditos_necesarios,
    50→            'creditos_necesarios'  => $creditos_necesarios,
    51→            'creditos_disponibles' => $creditos_disponibles,
    52→            'servicio_ia_id'       => $servicio->id,
    53→            'empresa_plan_id'      => $empresa_plan_id
    54→        ];
    55→
    56→        if (!$servicio) {
    57→            $datos_creditos['tiene_creditos']      = true;
    58→            $datos_creditos['creditos_necesarios'] = 0;
    59→            $datos_creditos['servicio_ia_id']      = null;
    60→        }
    61→
    62→        if ($creditos_necesarios <= 0) {
    63→            $datos_creditos['tiene_creditos']      = true;
    64→            $datos_creditos['creditos_necesarios'] = 0;
    65→        }
    66→
    67→        if (!$empresa_plan_id) {
    68→            $datos_creditos['tiene_creditos'] = false;
    69→        }
    70→
    71→        return $datos_creditos;
    72→    }
    73→
    74→    /**
    75→     * Obtener créditos configurados para un servicio.
    76→     *
    77→     * @param string $endpoint Endpoint.
    78→     * @return ?int
    79→     * @author Sergio Vargas
    80→     * @author Daniel Beltrán
    81→     */
    82→    public function obtenerCreditosServicio(string $endpoint): ?int {
    83→        $servicio = $this->obtenerServicioPorEndpoint($endpoint);
    84→        return $servicio?->consume_creditos;
    85→    }
    86→
    87→    /**
    88→     * Preparar datos de consumo para el tracking.
    89→     *
    90→     * @param ?int $empresa_plan_id ID del plan de empresa.
    91→     * @param string $endpoint Endpoint.
    92→     * @param ?int $empresa_id ID de la empresa.
    93→     * @return array
    94→     * @author Sergio Vargas
    95→     * @author Daniel Beltrán
    96→     */
    97→    public function prepararConsumo(?int $empresa_plan_id, string $endpoint, ?int $empresa_id = null): array {
    98→        $servicio = $this->obtenerServicioPorEndpoint($endpoint);
    99→
   100→        return [
   101→            'empresa_plan_id' => $this->resolverEmpresaPlanId($empresa_plan_id, $empresa_id),
   102→            'servicio_ia_id'  => $servicio?->id,
   103→            'creditos'        => $servicio?->consume_creditos ?? 0,
   104→        ];
   105→    }
   106→
   107→    /**
   108→     * Resolver servicio IA por endpoint normalizado.
   109→     *
   110→     * @param string $endpoint Endpoint.
   111→     * @return ?ServicioIA
   112→     * @author Sergio Vargas
   113→     * @author Daniel Beltrán
   114→     */
   115→    private function obtenerServicioPorEndpoint(string $endpoint): ?ServicioIA {
   116→        $servicio = $this->servicio_ia_repositorio->getUnoAvanzado(new Request([
   117→            'endpoint' => $this->normalizarEndpoint($endpoint)
   118→        ]));
   119→
   120→        if ($servicio && !$servicio->activo) {
   121→            return null;
   122→        }
   123→
   124→        return $servicio;
   125→    }
   126→
   127→    /**
   128→     * Normalizar endpoint para búsqueda.
   129→     *
   130→     * @param string $endpoint Endpoint.
   131→     * @return string
   132→     * @author Sergio Vargas
   133→     * @author Daniel Beltrán
   134→     */
   135→    private function normalizarEndpoint(string $endpoint): string {
   136→        return preg_replace('/\.(whisper|gpt)$/', '', trim($endpoint, '/'));
   137→    }
   138→
   139→    /**
   140→     * Resolver empresa_plan_id desde contexto.
   141→     *
   142→     * @param ?int $empresa_plan_id ID del plan de empresa.
   143→     * @param ?int $empresa_id ID de la empresa.
   144→     * @return ?int
   145→     * @author Sergio Vargas
   146→     * @author Daniel Beltrán
   147→     */
   148→    private function resolverEmpresaPlanId(?int $empresa_plan_id, ?int $empresa_id = null): ?int {
   149→        if ($empresa_plan_id) {
   150→            return $empresa_plan_id;
   151→        }
   152→
   153→        if (!$empresa_id) {
   154→            return null;
   155→        }
   156→
   157→        $plan = $this->empresa_plan_repositorio->planActivo($empresa_id, TipoPrueba::SELECCION_ID);
   158→
   159→        return $plan?->id;
   160→    }
   161→}
