     3→namespace App\Http\Services\IA;
     4→
     5→use Carbon\Carbon;
     6→use App\Models\Planes\Plan;
     7→use Illuminate\Support\Str;
     8→use Illuminate\Http\Request;
     9→use App\Models\IA\ConsumoCredito;
    10→use App\Http\Services\BaseService;
    11→use Illuminate\Support\Collection;
    12→use App\Http\Services\Empresas\EmpresaPlanService;
    13→use App\Http\Repositories\IA\ConsumoCreditoRepository;
    14→use App\Http\Repositories\Empresas\EmpresaPlanRepository;
