    16→
    17→class ConsumoCreditoService extends BaseService
    18→{
    19→    /**
    20→     * Constructor.
    21→     *
    22→     * @param ConsumoCreditoRepository $repositorio Repositorio de consumos de créditos.
    23→     * @param ServicioPrecioTokenService $precio_token_servicio Servicio de precios de tokens.
    24→     * @param EmpresaPlanRepository $empresa_plan_repositorio Repositorio de planes de empresa.
    25→     * @param EmpresaPlanService $empresa_plan_servicio Servicio de planes de empresa.
    26→     * @param EmpresaUsuarioRepository $empresa_usuario_repositorio Repositorio de empresas de usuarios.
    27→     * @return void
    28→     * @author Sergio Vargas
    29→     * @author Daniel Beltrán
    30→     * @author Andres Gonzalez
    31→     */
    32→    public function __construct(
    33→        ConsumoCreditoRepository $repositorio,
    34→        private readonly ServicioPrecioTokenService $precio_token_servicio,
    35→        private readonly EmpresaPlanRepository $empresa_plan_repositorio,
    36→        private readonly EmpresaPlanService $empresa_plan_servicio,
    37→        private readonly EmpresaUsuarioRepository $empresa_usuario_repositorio
    38→    ) {
    39→        parent::__construct($repositorio);
    40→    }
    41→
    42→    /**
    43→     * Registra el consumo de tokens desde la respuesta de la API de IA.
    44→     *
    45→     * Maneja tanto el formato nuevo (usage_whisper, usage_gpt) como el formato legacy (usage).
    46→     *
    47→     * @param string $ruta_endpoint Ruta del endpoint llamado (ej: 'generar-vacante').
    48→     * @param array $respuesta_ia Respuesta completa de la API de IA.
    49→     * @param ?int $usuario_id ID del usuario que realizó la petición.
    50→     * @param ?int $empresa_id ID de la empresa del usuario.
    51→     * @param array $contexto Array con agenda_id, vacante_id, etc.
    52→     * @param ?string $payload_peticion JSON del payload enviado.
    53→     * @param ?int $latencia_ms Latencia de la petición en milisegundos.
    54→     * @return ?ConsumoCredito
    55→     * @author Sergio Vargas
    56→     * @author Daniel Beltrán
    57→     * @author Andres Gonzalez
    58→     */
    59→    public function registrarConsumoDesdeRespuesta(
    60→        string $ruta_endpoint,
    61→        array $respuesta_ia,
    62→        ?int $usuario_id = null,
    63→        ?int $empresa_id = null,
    64→        ?array $contexto = [],
    65→        ?string $payload_peticion = null,
    66→        ?int $latencia_ms = null
    67→    ): ?ConsumoCredito {
    68→        // Detectar si hay múltiples metadata de uso (formato nuevo)
    69→        $uso_whisper     = $respuesta_ia['usage_whisper'] ?? null;
    70→        $uso_gpt         = $respuesta_ia['usage_gpt'] ?? null;
    71→        $ultimo_registro = null;
    72→
    73→        // Registrar Whisper si existe
    74→        if ($uso_whisper) {
    75→            $ultimo_registro = $this->registrarConsumoIndividual(
    76→                ruta_endpoint: $ruta_endpoint . '.whisper',
    77→                usage: $uso_whisper,
    78→                respuesta_ia: $respuesta_ia,
    79→                usuario_id: $usuario_id,
    80→                empresa_id: $empresa_id,
    81→                contexto: $contexto,
    82→                payload_peticion: $payload_peticion,
    83→                latencia_ms: $latencia_ms
    84→            );
    85→        }
    86→
    87→        // Registrar GPT si existe
    88→        if ($uso_gpt) {
    89→            $ultimo_registro = $this->registrarConsumoIndividual(
    90→                ruta_endpoint: $ruta_endpoint . '.gpt',
    91→                usage: $uso_gpt,
    92→                respuesta_ia: $respuesta_ia,
    93→                usuario_id: $usuario_id,
    94→                empresa_id: $empresa_id,
    95→                contexto: $contexto,
    96→                payload_peticion: $payload_peticion,
    97→                latencia_ms: $latencia_ms
    98→            );
    99→        }
   100→
   101→        // Fallback: Si solo hay "usage" (formato legacy)
   102→        $usage_legacy = $respuesta_ia['usage'] ?? null;
   103→
   104→        if ($usage_legacy && !$uso_whisper && !$uso_gpt) {
   105→            $ultimo_registro = $this->registrarConsumoIndividual(
   106→                ruta_endpoint: $ruta_endpoint,
   107→                usage: $usage_legacy,
   108→                respuesta_ia: $respuesta_ia,
   109→                usuario_id: $usuario_id,
   110→                empresa_id: $empresa_id,
   111→                contexto: $contexto,
   112→                payload_peticion: $payload_peticion,
   113→                latencia_ms: $latencia_ms
   114→            );
   115→        }
   116→
   117→        // Validar y actualizar estado del plan según créditos restantes
   118→        $this->actualizarEstadoPlanSegunCreditos();
   119→
   120→        return $ultimo_registro;
   121→    }
   122→
   123→    /**
   124→     * Actualiza el estado del plan de créditos según el porcentaje de créditos restantes.
   125→     *
   126→     * @return void
   127→     * @author Andres Gonzalez
   128→     */
   129→    private function actualizarEstadoPlanSegunCreditos(): void {
   130→        $empresa = $this->empresa_usuario_repositorio->obtenerEmpresaActualUsuario();
   131→        $plan_creditos = $this->empresa_plan_servicio->creditosDisponiblesEmpresa($empresa->id, new Request());
   132→
   133→        if (!$plan_creditos) {
   134→            return;
   135→        }
   136→
   137→        $empresa_plan_id = $plan_creditos['empresa_plan_id'] ?? null;
   138→        $creditos_plan = $plan_creditos['creditos_plan'] ?? 0;
   139→        $creditos_disponibles = $plan_creditos['creditos_disponibles'] ?? 0;
   140→
   141→        if (!$empresa_plan_id || $creditos_plan <= 0) {
   142→            return;
   143→        }
   144→
   145→        $porcentaje_restante = ($creditos_disponibles / $creditos_plan) * 100;
   146→
   147→        if ($creditos_disponibles <= 0) {
   148→            $this->empresa_plan_repositorio->actualizar($empresa_plan_id, ['estado_id' => Plan::ESTADO_VENCIDO_ID]);
   149→        } elseif ($porcentaje_restante < 20) {
   150→            $this->empresa_plan_repositorio->actualizar($empresa_plan_id, ['estado_id' => Plan::ESTADO_POR_VENCER_ID]);
   151→        }
   152→    }
