   154→    /**
   155→     * Registra un consumo individual de tokens desde una metadata de uso específica.
   156→     *
   157→     * @param string $ruta_endpoint Ruta del endpoint llamado.
   158→     * @param array $usage Metadata de uso específica.
   159→     * @param array $respuesta_ia Respuesta completa de la API de IA.
   160→     * @param ?int $usuario_id ID del usuario que realizó la petición.
   161→     * @param ?int $empresa_id ID de la empresa del usuario.
   162→     * @param array $contexto Array con agenda_id, vacante_id, etc.
   163→     * @param ?string $payload_peticion JSON del payload enviado.
   164→     * @param ?int $latencia_ms Latencia de la petición en milisegundos.
   165→     * @return ?ConsumoCredito
   166→     * @author Sergio Vargas
   167→     * @author Daniel Beltrán
   168→     */
   169→    private function registrarConsumoIndividual(
   170→        string $ruta_endpoint,
   171→        array $usage,
   172→        array $respuesta_ia,
   173→        ?int $usuario_id,
   174→        ?int $empresa_id,
   175→        array $contexto,
   176→        ?string $payload_peticion,
   177→        ?int $latencia_ms
   178→    ): ?ConsumoCredito {
   179→        $vacante_id = $contexto['vacante_id'] ?? null;
   180→
   181→        if (!$vacante_id && $payload_peticion) {
   182→            $decoded = json_decode($payload_peticion, true);
   183→
   184→            if (is_array($decoded)) {
   185→                $vacante_id = data_get($decoded, 'vacante_id')
   186→                    ?? data_get($decoded, 'vacanteId')
   187→                    ?? data_get($decoded, 'id')
   188→                    ?? data_get($decoded, 'vacante.id');
   189→
   190→                if ($vacante_id && !is_numeric($vacante_id)) {
   191→                    $vacante_id = null;
   192→                } elseif (is_numeric($vacante_id)) {
   193→                    $vacante_id = (int) $vacante_id;
   194→                }
   195→            }
   196→        }
   197→
   198→        // Usar el usage recibido como parámetro (ya sea usage_whisper o usage_gpt)
   199→        $modelo = $usage['model'] ?? 'desconocido';
   200→
   201→        // Preparar metadata adicional para cálculo de costo (necesario para Whisper)
   202→        $metadata_adicional = [];
   203→
   204→        if (isset($usage['audio_duration_minutes'])) {
   205→            $metadata_adicional['audio_duration_minutes'] = $usage['audio_duration_minutes'];
   206→        }
   207→
   208→        if (isset($usage['audio_duration_seconds'])) {
   209→            $metadata_adicional['audio_duration_seconds'] = $usage['audio_duration_seconds'];
   210→        }
   211→
   212→        // Calcular costo estimado (pasando metadata adicional para Whisper)
   213→        $costo_estimado = $this->precio_token_servicio->calcularCosto(
   214→            $modelo,
   215→            $usage['prompt_tokens'] ?? 0,
   216→            $usage['completion_tokens'] ?? 0,
   217→            $metadata_adicional ?: null
   218→        );
   219→
   220→        // Generar huella digital
   221→        $huella_peticion = null;
   222→
   223→        if ($payload_peticion) {
   224→            $huella_peticion = hash('sha256', $payload_peticion);
   225→        }
   226→
   227→        // Determinar estado de la respuesta
   228→        // Si tiene usage, asumimos que fue exitoso (FastAPI ya lanzaría excepción si hubo error)
   229→        $estado = 'exitoso';
   230→
   231→        if (isset($respuesta_ia['status_code'])) {
   232→            $estado = $respuesta_ia['status_code'] === 200 ? 'exitoso' : 'error';
   233→        } elseif (isset($respuesta_ia['error'])) {
   234→            $estado = 'error';
   235→        }
   236→
   237→        // Preparar datos
   238→        // Determinar extracto de la respuesta (soportar distintos nombres usados por FastAPI)
   239→        $contenido_respuesta = $respuesta_ia['data']
   240→            ?? $respuesta_ia['vacante']
   241→            ?? $respuesta_ia['preguntas']
   242→            ?? $respuesta_ia['pruebas']
   243→            ?? $respuesta_ia['calificacion']
   244→            ?? null;
   245→
   246→        // Si no se encontrИ un payload claro, usar la respuesta completa sin la secciИn usage
   247→        if ($contenido_respuesta === null) {
   248→            $contenido_respuesta = collect($respuesta_ia)->except('usage')->all();
   249→        }
   250→
   251→        $datos = [
   252→            'usuario_id'         => $usuario_id,
   253→            'empresa_id'         => $empresa_id,
   254→            'empresa_plan_id'    => $contexto['empresa_plan_id'] ?? null,
   255→            'servicio_ia_id'     => $contexto['servicio_ia_id'] ?? null,
   256→            'creditos'           => $contexto['creditos'] ?? 0,
   257→            'ruta_endpoint'      => $ruta_endpoint,
   258→            'nombre_servicio'    => $usage['service_name'] ?? null,
   259→            'modelo'             => $modelo,
   260→            'tokens_entrada'     => $usage['prompt_tokens'] ?? 0,
   261→            'tokens_salida'      => $usage['completion_tokens'] ?? 0,
   262→            'tokens_totales'     => $usage['total_tokens'] ?? 0,
   263→            'caracteres_entrada' => $usage['input_chars'] ?? null,
   264→            'costo_estimado_usd' => $costo_estimado,
   265→            'huella_peticion'    => $huella_peticion,
   266→            'extracto_peticion'  => $payload_peticion ? Str::limit($payload_peticion, 500) : null,
   267→            'extracto_respuesta' => $contenido_respuesta ? Str::limit(json_encode($contenido_respuesta), 500) : null,
   268→            'estado'             => $estado,
   269→            'latencia_ms'        => $latencia_ms,
   270→            'mensaje_error'      => $respuesta_ia['error'] ?? null,
   271→            'agenda_id'          => $contexto['agenda_id'] ?? null,
   272→            'vacante_id'         => $vacante_id
   273→        ];
   274→
   275→        return $this->repositorio->crear($datos);
   276→    }
