    60→    /**
    61→     * Guardar un plan de empresa.
    62→     *
    63→     * @param Request $request Contenido de la petición.
    64→     * @return EmpresaPlan
    65→     * @author Sergio Vargas
    66→     * @author Daniel Beltrán
    67→     * @author Andres Morales
    68→     * @author Jair Carvajal
    69→     * @author Andres Gonzalez
    70→     */
    71→    public function guardar(Request $request): EmpresaPlan {
    72→        $bandera_enviar_correo = false;
    73→        $campos_excluidos      = ['_token', '_method', 'id', 'tipo_prueba_id', 'factura', 'vigencia', 'pruebas', 'funcionalidades'];
    74→        $es_solo_creditos      = $request->filled('plan_credito_id') && !$request->filled('plan_id');
    75→        $plan                  = null;
    76→        $plan_activo           = null;
    77→        $vigencia              = null;
    78→
    79→        if (!$es_solo_creditos) {
    80→            $plan        = $this->plan_repositorio->getUno($request->plan_id);
    81→            $plan_activo = $this->repositorio->planActivo($request->empresa_id, $request->tipo_prueba_id);
    82→            $vigencia    = $plan->vigencia;
    83→        } else {
    84→            $request->merge(['plan_id' => null]);
    85→            $plan_activo = $this->repositorio->planCreditosActivo($request->empresa_id);
    86→        }
    87→
    88→        if ($request->filled('plan_credito_id') && !$request->filled('creditos')) {
    89→            $plan_credito = $this->plan_credito_repositorio->getUno($request->plan_credito_id);
    90→
    91→            if ($plan_credito) {
    92→                $request->merge(['creditos' => $plan_credito->creditos]);
    93→            }
    94→        } elseif (!$request->filled('plan_credito_id')) {
    95→            $request->merge([
    96→                'plan_credito_id' => null,
    97→                'creditos'        => 0
    98→            ]);
    99→        }
   100→
   101→        if (!$es_solo_creditos) {
   102→            if ($request->filled('vigencia')) {
   103→                $vigencia = $request->vigencia;
   104→            }
   105→
   106→            if (!is_null($plan_activo)) {
   107→                if ($request->filled('id')) {
   108→                    // Si el ID recibido es igual al plan activo, no se calcula vigencia
   109→                    if ($request->id === $plan_activo->id) {
   110→                        goto omitir_calcular_vigencia;
   111→                    }
   112→
   113→                    // Si la vigencia no ha sido modificada respecto al plan base, no se calcula vigencia
   114→                    if ($vigencia == $request->vigencia) {
   115→                        array_push($campos_excluidos, 'fecha_inicio', 'fecha_vencimiento');
   116→                        goto omitir_calcular_vigencia;
   117→                    }
   118→                }
   119→
   120→                // Poner el nuevo plan como proximo
   121→                $request->merge(['estado_id' => Plan::ESTADO_PROXIMO_ID]);
   122→
   123→                // Validar si el plan activo tiene fecha de vencimiento y el plan seleccionado tiene vigencia
   124→                if (!is_null($plan_activo->fecha_vencimiento) && !is_null($plan->vigencia)) {
   125→                    // Agregar las nuevas fechas al nuevo plan
   126→                    $hoy          = now();
   127→                    $fecha_inicio = ($hoy > $plan_activo->fecha_vencimiento) ? $hoy->copy() : Carbon::createFromDate($plan_activo->fecha_vencimiento)->addDay();
   128→                    $request->merge($this->calcularFechasVigencia($vigencia, $fecha_inicio));
   129→                }
   130→            } else {
   131→                $request->merge(['estado_id' => Plan::ESTADO_ACTIVO_ID]);
   132→
   133→                // Validar si el plan seleccionado tiene vigencia
   134→                if (!is_null($plan->vigencia)) {
   135→                    $request->merge($this->calcularFechasVigencia($vigencia));
   136→                }
   137→            }
   138→        } else {
   139→            $request->merge([
   140→                'fecha_inicio'      => null,
   141→                'fecha_vencimiento' => null
   142→            ]);
   143→
   144→            if (!is_null($plan_activo)) {
   145→                $request->merge(['estado_id' => Plan::ESTADO_PROXIMO_ID]);
   146→            } else {
   147→                $request->merge(['estado_id' => Plan::ESTADO_ACTIVO_ID]);
   148→            }
   149→        }
   150→
   151→        omitir_calcular_vigencia:
   152→
   153→        if (!$es_solo_creditos) {
   154→            if (!$request->filled('unidades')) {
   155→                $request->merge(['unidades' => $plan->unidades]);
   156→            } elseif ($request->unidades != $plan->unidades) {
   157→                $request->merge(['plan_original' => false]);
   158→            }
   159→
   160→            if (!$request->filled('cantidad_ofertas_laborales')) {
   161→                $request->merge(['cantidad_ofertas_laborales' => $plan->cantidad_ofertas_laborales]);
   162→            } elseif ($request->cantidad_ofertas_laborales != $plan->cantidad_ofertas_laborales) {
   163→                $request->merge(['plan_original' => false]);
   164→            }
   165→
   166→            if (!$request->filled('cantidad_usuarios')) {
   167→                $request->merge(['cantidad_usuarios' => $plan->cantidad_usuarios]);
   168→            } elseif ($request->cantidad_usuarios != $plan->cantidad_usuarios) {
   169→                $request->merge(['plan_original' => false]);
   170→            }
   171→
   172→            if (!$request->filled('cantidad_pruebas')) {
   173→                $request->merge(['cantidad_pruebas' => $plan->cantidad_pruebas]);
   174→            } elseif ($request->cantidad_pruebas != $plan->cantidad_pruebas) {
   175→                $request->merge(['plan_original' => false]);
   176→            }
   177→
   178→            // Comprobar si se modificaron las funcionalidades respecto al plan original
   179→            if (!is_null($plan->funcionalidades) && $request->filled('funcionalidades')) {
   180→                $funcionalidades_originales = $plan->funcionalidades->pluck('id')->toArray();
   181→                $primera_comparacion        = array_diff($request->funcionalidades, $funcionalidades_originales);
   182→                $segunda_comparacion        = array_diff($funcionalidades_originales, $request->funcionalidades);
   183→
   184→                if (count([...$primera_comparacion, ...$segunda_comparacion]) > 0) {
   185→                    $request->merge(['plan_original' => false]);
   186→                }
   187→            }
   188→        }
   189→
   190→        if ($request->filled('id')) {
   191→            // Eliminar la factura actual
   192→            if ($request->hasFile('factura')) {
   193→                $archivo = $this->archivo_repositorio->obtenerPorModelo('App\Models\Empresas\EmpresaPlan', $request->id);
   194→
   195→                if (!is_null($archivo)) {
   196→                    $this->eliminarArchivoDesdeRuta($archivo->ruta);
   197→                    $this->archivo_repositorio->eliminar($archivo->id);
   198→                }
   199→            }
   200→
   201→            $plan_asignado = $this->repositorio->actualizar($request->id, $request->except($campos_excluidos));
   202→        } else {
   203→            $bandera_enviar_correo = true;
   204→            $request->merge(['usuario_id' => auth()->user()->id]);
   205→            $plan_asignado = $this->repositorio->crear($request->except($campos_excluidos));
   206→        }
   207→
   208→        if ($request->filled('pruebas')) {
   209→            $this->repositorio->sincronizarPruebas($plan_asignado, $request->pruebas);
   210→        }
   211→
   212→        if ($request->filled('funcionalidades')) {
   213→            $this->repositorio->sincronizarFuncionalidades($plan_asignado, $request->funcionalidades);
   214→        }
   215→
   216→        // Guardar la factura
   217→        if ($request->hasFile('factura')) {
   218→            $this->guardarFactura($plan_asignado->id, $request->empresa_id, $request->file('factura'));
   219→        }
   220→
   221→        if ($bandera_enviar_correo) {
   222→            $empresa      = $this->empresa_repositorio->getUno($request->empresa_id);
   223→            $usuario      = $empresa->usuarios[0];
   224→            $empresa_plan = $this->repositorio->getUno($plan_asignado->id);
   225→            $titulo       = 'Usuario principal de la empresa creado';
   226→            Mail::send(new CrearUsuarioMail($usuario, Password::createToken($usuario), $titulo, $titulo, '', $empresa, $empresa_plan));
   227→        }
   228→
   229→        return $plan_asignado;
   230→    }
