   232→    /**
   233→     * Renovar el plan asignado.
   234→     *
   235→     * @param int $id ID del plan de la empresa que se quiere renovar.
   236→     * @throws Exception Si el plan que se quiere renovar está en estado 'próximo'.
   237→     * @return EmpresaPlan Plan de la empresa renovado.
   238→     * @author Daniel Beltrán
   239→     */
   240→    public function renovar(int $id): EmpresaPlan {
   241→        $empresa_plan = $this->repositorio->getUno($id);
   242→
   243→        if ($empresa_plan->estado_id === Plan::ESTADO_PROXIMO_ID) {
   244→            throw new Exception('No se puede renovar un plan que no ha entrado en vigencia');
   245→        }
   246→
   247→        $request = new Request(Arr::except($empresa_plan->toArray(), ['id', 'renovando', 'por_facturar', 'plan', 'estado', 'vigencia_dias']));
   248→
   249→        $request->merge(['por_facturar' => true]);
   250→
   251→        if (in_array($empresa_plan->estado_id, [Plan::ESTADO_ACTIVO_ID, Plan::ESTADO_POR_VENCER_ID])) {
   252→            // Cuando el plan actual está activo o por vencer, se asigna el estado 'próximo'
   253→            $request->merge(['estado_id' => Plan::ESTADO_PROXIMO_ID]);
   254→        } elseif (in_array($empresa_plan->estado_id, [Plan::ESTADO_INACTIVO_ID, Plan::ESTADO_VENCIDO_ID])) {
   255→            // Cuando el plan actual está inactivo o vencido, se asigna el estado 'activo'
   256→            $request->merge(['estado_id' => Plan::ESTADO_ACTIVO_ID]);
   257→        }
   258→
   259→        $this->verificarYAsignarTiempo($empresa_plan, $request);
   260→
   261→        $plan_renovado = $this->repositorio->crear($request->toArray());
   262→
   263→        if ($request->filled('pruebas')) {
   264→            $this->repositorio->sincronizarPruebas($plan_renovado, $request->pruebas);
   265→        }
   266→
   267→        if ($request->filled('funcionalidades')) {
   268→            $this->repositorio->sincronizarFuncionalidades($plan_renovado, $request->funcionalidades);
   269→        }
   270→
   271→        return $plan_renovado;
   272→    }
