   288→    /**
   289→     * Calcular las fechas en las que el plan estará vigente.
   290→     *
   291→     * @param int $vigencia Cantidad de días de vigencia.
   292→     * @param Carbon|null $fecha_inicio_vigencia Fecha en la que inicia la vigencia.
   293→     * @return array Fechas de inicio y vencimiento.
   294→     * @author Daniel Beltrán
   295→     */
   296→    public function calcularFechasVigencia(int $vigencia, ?Carbon $fecha_inicio_vigencia = null): array {
   297→        $hoy = now();
   298→
   299→        $fecha_inicio = !is_null($fecha_inicio_vigencia) ? $fecha_inicio_vigencia : $hoy;
   300→
   301→        // Si es múltiplo de 3, entonces la vigencia es en meses
   302→
   303→        if (($vigencia % 3) === 0 && gettype($vigencia / 30) === 'integer') {
   304→            $meses = $vigencia / 30;
   305→
   306→            $fecha_vencimiento = $fecha_inicio->copy()->addMonths($meses);
   307→
   308→            if ($fecha_inicio->day === 1) {
   309→                $fecha_vencimiento->subDay();
   310→            }
   311→        } else {
   312→            $vigencia = $vigencia < 0 ? abs($vigencia) : $vigencia; // Convierte los valores negativos a positivos
   313→
   314→            $fecha_vencimiento = $fecha_inicio->copy()->addDays($vigencia);
   315→        }
   316→
   317→        return [
   318→            'fecha_inicio' => $fecha_inicio->format('Y-m-d'),
   319→
   320→            'fecha_vencimiento' => $fecha_vencimiento->format('Y-m-d')
   321→        ];
   322→    }
