   332→    public function facturar(int $id, Request $request): EmpresaPlan {
   333→        $empresa_plan = $this->repositorio->getUno($id);
   334→
   335→        if ($request->filled('vigencia')) {
   336→            $request->merge($this->calcularFechasVigencia($request->vigencia, $empresa_plan->fecha_inicio));
   337→        }
   338→
   339→        $request->merge(['por_facturar' => false]);
   340→
   341→        $this->repositorio->sincronizarPruebas($empresa_plan, $request->pruebas);
   342→
   343→        $this->repositorio->sincronizarFuncionalidades($empresa_plan, $request->funcionalidades);
   344→
   345→        $this->guardarFactura($id, $empresa_plan->empresa_id, $request->file('factura'));
   346→
   347→        return $this->repositorio->actualizar($id, $request->except('_token', '_method', 'pruebas', 'factura', 'vigencia', 'funcionalidades'));
