    83→        } else {
    84→            $request->merge(['plan_id' => null]);
    85→            $plan_activo = $this->repositorio->planCreditosActivo($request->empresa_id);
    86→        }
    87→
    88→        if ($request->filled('plan_credito_id') && !$request->filled('creditos')) {
    89→            $plan_credito = $this->plan_credito_repositorio->getUno($request->plan_credito_id);
    90→
    91→            if ($plan_credito) {
    92→                $request->merge(['creditos' => $plan_credito->creditos]);
    93→            }
    94→        } elseif (!$request->filled('plan_credito_id')) {
    95→            $request->merge([
    96→                'plan_credito_id' => null,
    97→                'creditos'        => 0
    98→            ]);
    99→        }
   100→
   101→        if (!$es_solo_creditos) {
   102→            if ($request->filled('vigencia')) {
   103→                $vigencia = $request->vigencia;
   104→            }
   105→
   106→            if (!is_null($plan_activo)) {
   107→                if ($request->filled('id')) {
   108→                    // Si el ID recibido es igual al plan activo, no se calcula vigencia
   109→                    if ($request->id === $plan_activo->id) {
   110→                        goto omitir_calcular_vigencia;
   111→                    }
   112→
   113→                    // Si la vigencia no ha sido modificada respecto al plan base, no se calcula vigencia
   114→                    if ($vigencia == $request->vigencia) {
   115→                        array_push($campos_excluidos, 'fecha_inicio', 'fecha_vencimiento');
   116→                        goto omitir_calcular_vigencia;
   117→                    }
   118→                }
   119→
   120→                // Poner el nuevo plan como proximo
   121→                $request->merge(['estado_id' => Plan::ESTADO_PROXIMO_ID]);
   122→
   123→                // Validar si el plan activo tiene fecha de vencimiento y el plan seleccionado tiene vigencia
   124→                if (!is_null($plan_activo->fecha_vencimiento) && !is_null($plan->vigencia)) {
   125→                    // Agregar las nuevas fechas al nuevo plan
   126→                    $hoy          = now();
   127→                    $fecha_inicio = ($hoy > $plan_activo->fecha_vencimiento) ? $hoy->copy() : Carbon::createFromDate($plan_activo->fecha_vencimiento)->addDay();
   128→                    $request->merge($this->calcularFechasVigencia($vigencia, $fecha_inicio));
   129→                }
   130→            } else {
   131→                $request->merge(['estado_id' => Plan::ESTADO_ACTIVO_ID]);
   132→
   133→                // Validar si el plan seleccionado tiene vigencia
   134→                if (!is_null($plan->vigencia)) {
