   166→            if (!$request->filled('cantidad_usuarios')) {
   167→                $request->merge(['cantidad_usuarios' => $plan->cantidad_usuarios]);
   168→            } elseif ($request->cantidad_usuarios != $plan->cantidad_usuarios) {
   169→                $request->merge(['plan_original' => false]);
   170→            }
   171→
   172→            if (!$request->filled('cantidad_pruebas')) {
   173→                $request->merge(['cantidad_pruebas' => $plan->cantidad_pruebas]);
   174→            } elseif ($request->cantidad_pruebas != $plan->cantidad_pruebas) {
   175→                $request->merge(['plan_original' => false]);
   176→            }
   177→
   178→            // Comprobar si se modificaron las funcionalidades respecto al plan original
   179→            if (!is_null($plan->funcionalidades) && $request->filled('funcionalidades')) {
   180→                $funcionalidades_originales = $plan->funcionalidades->pluck('id')->toArray();
   181→                $primera_comparacion        = array_diff($request->funcionalidades, $funcionalidades_originales);
   182→                $segunda_comparacion        = array_diff($funcionalidades_originales, $request->funcionalidades);
   183→
   184→                if (count([...$primera_comparacion, ...$segunda_comparacion]) > 0) {
   185→                    $request->merge(['plan_original' => false]);
   186→                }
   187→            }
   188→        }
   189→
   190→        if ($request->filled('id')) {
   191→            // Eliminar la factura actual
   192→            if ($request->hasFile('factura')) {
   193→                $archivo = $this->archivo_repositorio->obtenerPorModelo('App\Models\Empresas\EmpresaPlan', $request->id);
   194→
   195→                if (!is_null($archivo)) {
   196→                    $this->eliminarArchivoDesdeRuta($archivo->ruta);
   197→                    $this->archivo_repositorio->eliminar($archivo->id);
   198→                }
   199→            }
   200→
   201→            $plan_asignado = $this->repositorio->actualizar($request->id, $request->except($campos_excluidos));
   202→        } else {
   203→            $bandera_enviar_correo = true;
   204→            $request->merge(['usuario_id' => auth()->user()->id]);
   205→            $plan_asignado = $this->repositorio->crear($request->except($campos_excluidos));
   206→        }
   207→
   208→        if ($request->filled('pruebas')) {
   209→            $this->repositorio->sincronizarPruebas($plan_asignado, $request->pruebas);
   210→        }
   211→
   212→        if ($request->filled('funcionalidades')) {
   213→            $this->repositorio->sincronizarFuncionalidades($plan_asignado, $request->funcionalidades);
   214→        }
   215→
   216→        // Guardar la factura
   217→        if ($request->hasFile('factura')) {
   218→            $this->guardarFactura($plan_asignado->id, $request->empresa_id, $request->file('factura'));
   219→        }
   220→
   221→        if ($bandera_enviar_correo) {
   222→            $empresa      = $this->empresa_repositorio->getUno($request->empresa_id);
   223→            $usuario      = $empresa->usuarios[0];
   224→            $empresa_plan = $this->repositorio->getUno($plan_asignado->id);
   225→            $titulo       = 'Usuario principal de la empresa creado';
   226→            Mail::send(new CrearUsuarioMail($usuario, Password::createToken($usuario), $titulo, $titulo, '', $empresa, $empresa_plan));
   227→        }
   228→
   229→        return $plan_asignado;
   230→    }
