   274→    /**
   275→     * Verificar si el plan es por tiempo y asignar el tiempo de vigencia en caso de serlo.
   276→     *
   277→     * @param EmpresaPlan $empresa_plan Plan de la empresa.
   278→     * @param Request $request Contenido de la petición.
   279→     * @return void
   280→     * @author Daniel Beltrán
   281→     */
   282→    public function verificarYAsignarTiempo(EmpresaPlan $empresa_plan, Request &$request): void {
   283→        if (!is_null($empresa_plan->fecha_inicio) && !is_null($empresa_plan->fecha_vencimiento)) {
   284→            $request->merge($this->calcularFechasVigencia($empresa_plan->plan->vigencia, $empresa_plan->fecha_vencimiento->addDay()));
   285→        }
   286→    }
