   636→     * @author José Manuel Jiménez
   637→     */
   638→    public function creditosDisponiblesEmpresa(int $empresa_id, Request $request): ?array {
   639→        $tipo_prueba_id = $request->filled('tipo_prueba_id')
   640→            ? (int) $request->tipo_prueba_id
   641→            : TipoPrueba::SELECCION_ID;
   642→
   643→        $empresa_plan = $this->repositorio->planActivo($empresa_id, $tipo_prueba_id);
   644→
   645→        if (!$empresa_plan) {
   646→            return null;
   647→        }
   648→
   649→        $empresa_plan->load('planCredito:id,nombre,creditos,precio');
   650→
   651→        return [
   652→            'empresa_id'           => $empresa_id,
