   174→                    contexto: $contexto_tracking
   175→                );
   176→            }
   177→
   178→            return $respuesta ?? [];
   179→        } catch (RequestException|ConnectionException $error) {
   180→            $this->registrarConsumoTokens(
   181→                ruta_endpoint: $ruta,
   182→                respuesta_ia: [
   183→                    'status_code' => 500,
   184→                    'error'       => $error->getMessage(),
   185→                ],
   186→                payload_peticion: $payload_peticion,
   187→                latencia_ms: (int) ((microtime(true) - $tiempo_inicio) * 1000),
   188→                contexto: $contexto_tracking
   189→            );
   190→
   191→            return [];
   192→        }
   193→    }
