   195→    /**
   196→     * Adjuntar parámetro de forma recursiva, manejando arrays anidados.
   197→     *
   198→     * @param mixed $peticion Instancia de la petición HTTP.
   199→     * @param string $nombre Nombre del parámetro.
   200→     * @param mixed $valor Valor del parámetro.
   201→     * @return void
   202→     * @author Sergio Vargas
   203→     * @author Daniel Beltrán
   204→     */
   205→    private function adjuntarParametroRecursivo(mixed $peticion, string $nombre, mixed $valor): void {
   206→        if ($valor instanceof UploadedFile) {
   207→            $peticion->attach($nombre, file_get_contents($valor->getRealPath()), $valor->getClientOriginalName());
   208→        } elseif (is_resource($valor)) {
   209→            $contenido      = stream_get_contents($valor);
   210→            $nombre_archivo = 'archivo.bin';
   211→            $peticion->attach($nombre, $contenido, $nombre_archivo);
   212→        } elseif (is_array($valor)) {
   213→            foreach ($valor as $key => $item) {
   214→                $this->adjuntarParametroRecursivo($peticion, "{$nombre}[{$key}]", $item);
   215→            }
   216→        } else {
   217→            $peticion->attach($nombre, (string) $valor);
   218→        }
   219→    }
