   317→
   318→            return $servicio_creditos->prepararConsumo($empresa_plan_id, $endpoint, $empresa_id);
   319→        } catch (Exception $error) {
   320→            return [
   321→                'empresa_plan_id' => $contexto['empresa_plan_id'] ?? null,
   322→                'servicio_ia_id'  => null,
   323→                'creditos'        => 0
   324→            ];
   325→        }
   326→    }
