   123→    /**
   124→     * Actualiza el estado del plan de créditos según el porcentaje de créditos restantes.
   125→     *
   126→     * @return void
   127→     * @author Andres Gonzalez
   128→     */
   129→    private function actualizarEstadoPlanSegunCreditos(): void {
   130→        $empresa = $this->empresa_usuario_repositorio->obtenerEmpresaActualUsuario();
   131→        $plan_creditos = $this->empresa_plan_servicio->creditosDisponiblesEmpresa($empresa->id, new Request());
   132→
   133→        if (!$plan_creditos) {
   134→            return;
   135→        }
   136→
   137→        $empresa_plan_id = $plan_creditos['empresa_plan_id'] ?? null;
   138→        $creditos_plan = $plan_creditos['creditos_plan'] ?? 0;
   139→        $creditos_disponibles = $plan_creditos['creditos_disponibles'] ?? 0;
   140→
   141→        if (!$empresa_plan_id || $creditos_plan <= 0) {
   142→            return;
   143→        }
   144→
   145→        $porcentaje_restante = ($creditos_disponibles / $creditos_plan) * 100;
   146→
   147→        if ($creditos_disponibles <= 0) {
   148→            $this->empresa_plan_repositorio->actualizar($empresa_plan_id, ['estado_id' => Plan::ESTADO_VENCIDO_ID]);
   149→        } elseif ($porcentaje_restante < 20) {
   150→            $this->empresa_plan_repositorio->actualizar($empresa_plan_id, ['estado_id' => Plan::ESTADO_POR_VENCER_ID]);
   151→        }
   152→    }
