   278→    /**
   279→     * Obtener historial de consumos de creditos por empresa.
   280→     *
   281→     * @param int $empresa_id ID de la empresa.
   282→     * @param array $filtros Filtros de búsqueda (desde, hasta, servicio_ia_id, usuario_id).
   283→     * @return Collection
   284→     * @author Sergio Vargas
   285→     * @author Jair Carvajal
   286→     */
   287→    public function historialConsumosEmpresa(int $empresa_id, array $filtros = []): Collection {
   288→        $desde          = null;
   289→        $hasta          = null;
   290→        $servicio_ia_id = !empty($filtros['servicio_ia_id']) ? (int) $filtros['servicio_ia_id'] : null;
   291→        $usuario_id     = !empty($filtros['usuario_id']) ? (int) $filtros['usuario_id'] : null;
   292→
   293→        if (!empty($filtros['desde'])) {
   294→            $desde = Carbon::parse($filtros['desde'])->startOfDay();
   295→        }
   296→
   297→        if (!empty($filtros['hasta'])) {
   298→            $hasta = Carbon::parse($filtros['hasta'])->endOfDay();
   299→        }
   300→
   301→        $consumos = $this->repositorio->historialPorEmpresa($empresa_id, $desde, $hasta, $servicio_ia_id, $usuario_id);
   302→
   303→        return $consumos->map(function (ConsumoCredito $consumo) {
   304→            $servicio = $consumo->servicioIA?->nombre
   305→                ?? $consumo->nombre_servicio
   306→                ?? $consumo->ruta_endpoint;
   307→
   308→            return [
   309→                'usuario'        => $consumo->usuario ? [
   310→                    'id'              => $consumo->usuario->id,
   311→                    'nombre_completo' => $consumo->usuario->nombre_completo
   312→                ] : null,
   313→                'servicio'       => $servicio,
   314→                'creditos'       => $consumo->creditos ?? 0,
   315→                'tokens_reales'  => $consumo->tokens_totales ?? 0,
   316→                'fecha_consumo'  => $consumo->created_at
   317→            ];
   318→        });
   319→    }
   320→}
