    38→        $servicio             = $this->obtenerServicioPorEndpoint($endpoint);
    39→        $creditos_necesarios  = $servicio?->consume_creditos ?? 0;
    40→        $empresa_plan_id      = $this->resolverEmpresaPlanId($empresa_plan_id, $empresa_id);
    41→        $creditos_disponibles = 0;
    42→
    43→        if ($empresa_plan_id) {
