    45→            $creditos_disponibles = $empresa_plan?->creditos_restantes ?? 0;
    46→        }
    47→
    48→        $datos_creditos = [
    49→            'tiene_creditos'       => $creditos_disponibles >= $creditos_necesarios,
    50→            'creditos_necesarios'  => $creditos_necesarios,
    51→            'creditos_disponibles' => $creditos_disponibles,
    52→            'servicio_ia_id'       => $servicio->id,
    53→            'empresa_plan_id'      => $empresa_plan_id
    54→        ];
    55→
    56→        if (!$servicio) {
    57→            $datos_creditos['tiene_creditos']      = true;
    58→            $datos_creditos['creditos_necesarios'] = 0;
    59→            $datos_creditos['servicio_ia_id']      = null;
    60→        }
    61→
    62→        if ($creditos_necesarios <= 0) {
