    64→            $datos_creditos['creditos_necesarios'] = 0;
    65→        }
    66→
    67→        if (!$empresa_plan_id) {
    68→            $datos_creditos['tiene_creditos'] = false;
    69→        }
    70→
    71→        return $datos_creditos;
