   218→    private function establecerPlanProximoAVencer(EmpresaPlan $empresa_plan): void {
   219→        if ($empresa_plan->esSoloPlanCreditos()) {
   220→            $total_creditos = $empresa_plan->creditos;
   221→
   222→            if ($total_creditos > 0) {
   223→                $creditos_consumidos = $total_creditos - $empresa_plan->creditos_restantes;
   224→
