   249→            ) {
   250→                $this->empresa_plan_repositorio->actualizar($empresa_plan->id, ['estado_id' => Plan::ESTADO_POR_VENCER_ID]);
   251→            }
   252→        } else {
   253→            $fecha_actual = now()->startOfDay();
   254→
   255→            $fecha_vencimiento = Carbon::parse($empresa_plan->fecha_vencimiento)->startOfDay();
   256→
   257→            if (
   258→                intval($fecha_actual->diffInDays($fecha_vencimiento)) <= self::DIAS_RESTANTES_PLAN_PROXIMO_A_VENCER
   259→                && intval($fecha_actual->diffInDays($fecha_vencimiento)) >= 0
   260→            ) {
   261→                $this->empresa_plan_repositorio->actualizar($empresa_plan->id, ['estado_id' => Plan::ESTADO_POR_VENCER_ID]);
   262→            }
   263→        }
   264→    }
