   205→    private function adjuntarParametroRecursivo(mixed $peticion, string $nombre, mixed $valor): void {
   206→        if ($valor instanceof UploadedFile) {
   207→            $peticion->attach($nombre, file_get_contents($valor->getRealPath()), $valor->getClientOriginalName());
