   211→            $peticion->attach($nombre, $contenido, $nombre_archivo);
   212→        } elseif (is_array($valor)) {
   213→            foreach ($valor as $key => $item) {
   214→                $this->adjuntarParametroRecursivo($peticion, "{$nombre}[{$key}]", $item);
   215→            }
   216→        } else {
