   271→                $empresa_id
   272→            );
   273→
   274→            if (!$resultado['tiene_creditos']) {
   275→                return [
   276→                    'puede_llamar'         => false,
   277→                    'creditos_necesarios'  => $resultado['creditos_necesarios'],
   278→                    'creditos_disponibles' => $resultado['creditos_disponibles'],
   279→                    'empresa_plan_id'      => $resultado['empresa_plan_id'],
   280→                    'mensaje'              => 'No tienes créditos disponibles en este momento. Renueva tu plan o adquiere más créditos para continuar.'
   281→                ];
   282→            }
   283→
   284→            return [
   285→                'puede_llamar'         => true,
   286→                'creditos_necesarios'  => $resultado['creditos_necesarios'],
   287→                'creditos_disponibles' => $resultado['creditos_disponibles'],
   288→                'empresa_plan_id'      => $resultado['empresa_plan_id'],
   289→                'servicio_ia_id'       => $resultado['servicio_ia_id'],
   290→                'mensaje'              => 'ok'
   291→            ];
   292→        } catch (Exception $error) {
