   295→                'creditos_necesarios'  => 0,
   296→                'creditos_disponibles' => 0,
   297→                'empresa_plan_id'      => $contexto['empresa_plan_id'] ?? null,
   298→                'mensaje'              => 'ok'
   299→            ];
   300→        }
