   331→     * @param array $respuesta Respuesta.
   332→     * @return bool
   333→     * @author Sergio Vargas
   334→     * @author Daniel Beltrán
   335→     */
   336→    private function esRespuestaExitosa(array $respuesta): bool {
   337→        if (isset($respuesta['error'])) {
   338→            return false;
   339→        }
   340→
   341→        if (isset($respuesta['status_code'])) {
   342→            return (int) $respuesta['status_code'] === 200;
   343→        }
   344→
   345→        return true;
