/**
 * Obtener el proximo plan de creditos de la empresa.
 *
 * @param int $empresa_id ID de la empresa.
 * @param int|null $tipo_prueba_id ID del tipo de prueba (opcional).
 * @return EmpresaPlan|null
 * @author Sergio Vargas
 */
public function planProximoCreditos(int $empresa_id, ?int $tipo_prueba_id = null): ?EmpresaPlan {
    return $this->modelo::select('id', 'empresa_id', 'plan_id', 'plan_credito_id', 'estado_id', 'creditos', 'fecha_inicio', 'fecha_vencimiento', 'created_at')
    ->with(['planCredito:id,nombre,creditos,precio'])
    ->where([
        'empresa_id' => $empresa_id,
        'estado_id'  => 3
    ])
    ->whereNotNull('plan_credito_id')
    ->when($tipo_prueba_id, function ($query) use ($tipo_prueba_id) {
        $query->where(function ($q) use ($tipo_prueba_id) {
            $q->whereHas('plan', fn($plan) => $plan->where('tipo_prueba_id', $tipo_prueba_id))
              ->orWhereNull('plan_id');
        });
    })
    ->orderBy('created_at', 'ASC')
    ->first();
}

/**
 * Obtener el plan de creditos activo de la empresa.
 *
 * @param int $empresa_id ID de la empresa.
 * @param int|null $tipo_prueba_id ID del tipo de prueba (opcional).
 * @return EmpresaPlan|null
 * @author Sergio Vargas
