 * @param int|null $latencia_ms Latencia de la petición en milisegundos.
 * @return array Array de ConsumoCredito registrados.
 * @author [Tu nombre]
 */
public function registrarConsumoDividido(
    string $ruta_endpoint,
    array $respuesta_ia,
    array $distribucion,
    ?int $usuario_id = null,
    ?int $empresa_id = null,
    ?array $contexto = [],
    ?string $payload_peticion = null,
    ?int $latencia_ms = null
): array {
    $registros = [];

    foreach ($distribucion as $item) {
        // Activar plan próximo si es necesario
        if ($item['es_proximo']) {
            $this->empresa_plan_repositorio->actualizar(
                $item['empresa_plan_id'],
                ['estado_id' => Plan::ESTADO_ACTIVO_ID]
            );
