    10→use Illuminate\Database\Eloquent\Relations\BelongsTo;
    11→use Illuminate\Database\Eloquent\Factories\HasFactory;
    12→use Illuminate\Database\Eloquent\Relations\BelongsToMany;
    13→use OwenIt\Auditing\Contracts\Auditable as AuditableContract;
    14→
    15→class Plan extends Model implements AuditableContract
    16→{
    17→    use HasFactory, SoftDeletes, Auditable;
    18→
    19→    public const ESTADO_ACTIVO_ID     = 1;
    20→    public const ESTADO_INACTIVO_ID   = 2;
    21→    public const ESTADO_PROXIMO_ID    = 3;
    22→    public const ESTADO_POR_VENCER_ID = 4;
    23→    public const ESTADO_VENCIDO_ID    = 5;
    24→
    25→    protected $table = 'planes';
    26→
    27→    protected $fillable = [
    28→        'tipo_prueba_id',
    29→        'nombre',
    30→        'descripcion',
    31→        'unidades',
    32→        'cantidad_ofertas_laborales',
    33→        'vigencia',
    34→        'cantidad_usuarios',
    35→        'cantidad_pruebas',
    36→        'precio',
    37→        'en_promocion',
    38→        'porcentaje_descuento',
    39→        'fecha_inicio_promocion',
    40→        'fecha_fin_promocion',
    41→        'comentario',
    42→        'activo'
    43→    ];
    44→
    45→    protected $casts = [
    46→        'created_at' => 'datetime:d/m/Y',
    47→        'updated_at' => 'datetime:d/m/Y'
    48→    ];
    49→
    50→    /**
    51→     * Relación de muchos a uno con tipos_pruebas.
    52→     *
    53→     * @return BelongsTo
    54→     * @author Daniel Beltrán
    55→     */
    56→    public function tipoPrueba(): BelongsTo {
    57→        return $this->belongsTo(TipoPrueba::class);
    58→    }
    59→
    60→    /**
    61→     * Relación de muchos a muchos con funcionalidades.
    62→     *
    63→     * @return BelongsToMany
    64→     * @author Daniel Beltrán
    65→     */
    66→    public function funcionalidades(): BelongsToMany {
    67→        return $this->belongsToMany(Funcionalidad::class, 'funcionalidades_planes', 'plan_id', 'funcionalidad_id')->withTimestamps();
    68→    }
    69→
    70→    /**
    71→     * Relación de muchos a muchos con modulos.
    72→     *
    73→     * @return BelongsToMany
    74→     * @author Daniel Beltrán
    75→     */
    76→    public function modulos(): BelongsToMany {
    77→        return $this->belongsToMany(Modulo::class, 'modulos_planes', 'plan_id', 'modulo_id')->withTimestamps();
    78→    }
    79→
    80→    /**
    81→     * Relación de muchos a muchos con empresas.
    82→     *
    83→     * @return BelongsToMany
    84→     * @author Daniel Beltrán
    85→     */
    86→    public function empresas(): BelongsToMany {
    87→        return $this->belongsToMany(Empresa::class, 'empresas_planes', 'plan_id', 'empresa_id')
    88→        ->withPivot(['unidades', 'fecha_vencimiento', 'precio'])
    89→        ->withTimestamps();
    90→    }
    91→}
    92→

