    59→    /**
    60→     * Guardar un plan de empresa.
    61→     *
    62→     * @param Request $request Contenido de la petición.
    63→     * @return EmpresaPlan
    64→     * @author Sergio Vargas
    65→     * @author Daniel Beltrán
    66→     * @author Andres Morales
    67→     * @author Jair Carvajal
    68→     */
    69→    public function guardar(Request $request): EmpresaPlan {
    70→        $bandera_enviar_correo = false;
    71→        $campos_excluidos      = ['_token', '_method', 'id', 'tipo_prueba_id', 'factura', 'vigencia', 'pruebas', 'funcionalidades'];
    72→        $plan                  = $this->plan_repositorio->getUno($request->plan_id);
    73→        $plan_activo           = $this->repositorio->planActivo($request->empresa_id, $request->tipo_prueba_id);
    74→        $vigencia              = $plan->vigencia;
    75→
    76→        if ($request->filled('plan_credito_id') && !$request->filled('creditos')) {
    77→            $plan_credito = $this->plan_credito_repositorio->getUno($request->plan_credito_id);
    78→
    79→            if ($plan_credito) {
    80→                $request->merge(['creditos' => $plan_credito->creditos]);
    81→            }
    82→        } elseif (!$request->filled('plan_credito_id')) {
    83→            $request->merge([
    84→                'plan_credito_id' => null,
    85→                'creditos'        => null
    86→            ]);
    87→        }
    88→
    89→        if ($request->filled('vigencia')) {
    90→            $vigencia = $request->vigencia;
    91→        }
    92→
    93→        if (!is_null($plan_activo)) {
    94→            if ($request->filled('id')) {
    95→                // Si el ID recibido es igual al plan activo, no se calcula vigencia
    96→                if ($request->id === $plan_activo->id) {
    97→                    goto omitir_calcular_vigencia;
    98→                }
    99→
   100→                // Si la vigencia no ha sido modificada respecto al plan base, no se calcula vigencia
   101→                if ($vigencia == $request->vigencia) {
   102→                    array_push($campos_excluidos, 'fecha_inicio', 'fecha_vencimiento');
   103→                    goto omitir_calcular_vigencia;
   104→                }
   105→            }
   106→
   107→            // Poner el nuevo plan como próximo
   108→            $request->merge(['estado_id' => 3]);
   109→
   110→            // Validar si el plan activo tiene fecha de vencimiento y el plan seleccionado tiene vigencia
   111→            if (!is_null($plan_activo->fecha_vencimiento) && !is_null($plan->vigencia)) {
   112→                // Agregar las nuevas fechas al nuevo plan
   113→                $hoy          = now();
   114→                $fecha_inicio = ($hoy > $plan_activo->fecha_vencimiento) ? $hoy->copy() : Carbon::createFromDate($plan_activo->fecha_vencimiento)->addDay();
