   117→        } else {
   118→            $request->merge(['estado_id' => 1]);
   119→
   120→            // Validar si el plan seleccionado tiene vigencia
   121→            if (!is_null($plan->vigencia)) {
   122→                $request->merge($this->calcularFechasVigencia($vigencia));
   123→            }
   124→        }
   125→
   126→        omitir_calcular_vigencia:
   127→
   128→        if (!$request->filled('unidades')) {
   129→            $request->merge(['unidades' => $plan->unidades]);
   130→        } elseif ($request->unidades != $plan->unidades) {
   131→            $request->merge(['plan_original' => false]);
   132→        }
   133→
   134→        if (!$request->filled('cantidad_ofertas_laborales')) {
   135→            $request->merge(['cantidad_ofertas_laborales' => $plan->cantidad_ofertas_laborales]);
   136→        } elseif ($request->cantidad_ofertas_laborales != $plan->cantidad_ofertas_laborales) {
   137→            $request->merge(['plan_original' => false]);
   138→        }
   139→
   140→        if (!$request->filled('cantidad_usuarios')) {
   141→            $request->merge(['cantidad_usuarios' => $plan->cantidad_usuarios]);
   142→        } elseif ($request->cantidad_usuarios != $plan->cantidad_usuarios) {
   143→            $request->merge(['plan_original' => false]);
   144→        }
   145→
   146→        if (!$request->filled('cantidad_pruebas')) {
   147→            $request->merge(['cantidad_pruebas' => $plan->cantidad_pruebas]);
   148→        } elseif ($request->cantidad_pruebas != $plan->cantidad_pruebas) {
   149→            $request->merge(['plan_original' => false]);
   150→        }
   151→
   152→        // Comprobar si se modificaron las funcionalidades respecto al plan original
   153→        if (!is_null($plan->funcionalidades) && $request->filled('funcionalidades')) {
   154→            $funcionalidades_originales = $plan->funcionalidades->pluck('id')->toArray();
   155→            $primera_comparacion        = array_diff($request->funcionalidades, $funcionalidades_originales);
   156→            $segunda_comparacion        = array_diff($funcionalidades_originales, $request->funcionalidades);
   157→
   158→            if (count([...$primera_comparacion, ...$segunda_comparacion]) > 0) {
   159→                $request->merge(['plan_original' => false]);
   160→            }
   161→        }
   162→
   163→        if ($request->filled('id')) {
   164→            // Eliminar la factura actual
   165→            if ($request->hasFile('factura')) {
   166→                $archivo = $this->archivo_repositorio->obtenerPorModelo('App\Models\Empresas\EmpresaPlan', $request->id);
   167→
   168→                if (!is_null($archivo)) {
   169→                    $this->eliminarArchivoDesdeRuta($archivo->ruta);
   170→                    $this->archivo_repositorio->eliminar($archivo->id);
   171→                }
   172→            }
   173→
   174→            $plan_asignado = $this->repositorio->actualizar($request->id, $request->except($campos_excluidos));
   175→        } else {
   176→            $bandera_enviar_correo = true;
   177→            $request->merge(['usuario_id' => auth()->user()->id]);
   178→            $plan_asignado = $this->repositorio->crear($request->except($campos_excluidos));
   179→        }
   180→
   181→        if ($request->filled('pruebas')) {
   182→            $this->repositorio->sincronizarPruebas($plan_asignado, $request->pruebas);
   183→        }
   184→
   185→        if ($request->filled('funcionalidades')) {
   186→            $this->repositorio->sincronizarFuncionalidades($plan_asignado, $request->funcionalidades);
   187→        }
   188→
   189→        // Guardar la factura
   190→        if ($request->hasFile('factura')) {
   191→            $this->guardarFactura($plan_asignado->id, $request->empresa_id, $request->file('factura'));
   192→        }
   193→
   194→        if ($bandera_enviar_correo) {
   195→            $empresa      = $this->empresa_repositorio->getUno($request->empresa_id);
   196→            $usuario      = $empresa->usuarios[0];
   197→            $empresa_plan = $this->repositorio->getUno($plan_asignado->id);
   198→
   199→            $titulo = 'Usuario principal de la empresa creado';
   200→            Mail::send(new CrearUsuarioMail($usuario, Password::createToken($usuario), $titulo, $titulo, '', $empresa, $empresa_plan));
   201→        }
   202→
   203→        return $plan_asignado;
   204→    }
