   206→    /**
   207→     * Renovar el plan asignado.
   208→     *
   209→     * @param int $id ID del plan de la empresa que se quiere renovar.
   210→     * @throws Exception Si el plan que se quiere renovar está en estado 'próximo'.
   211→     * @return EmpresaPlan Plan de la empresa renovado.
   212→     * @author Daniel Beltrán
   213→     */
   214→    public function renovar(int $id): EmpresaPlan {
   215→        $empresa_plan = $this->repositorio->getUno($id);
   216→
   217→        if ($empresa_plan->estado_id === 3) {
   218→            throw new Exception('No se puede renovar un plan que no ha entrado en vigencia');
   219→        }
   220→
   221→        $request = new Request(Arr::except($empresa_plan->toArray(), ['id', 'renovando', 'por_facturar', 'plan', 'estado', 'vigencia_dias']));
   222→        $request->merge(['por_facturar' => true]);
   223→
   224→        if (in_array($empresa_plan->estado_id, [1, 4])) {
   225→            // Cuando el plan actual está activo o por vencer, se asigna el estado 'próximo'
   226→            $request->merge(['estado_id' => 3]);
   227→        } elseif (in_array($empresa_plan->estado_id, [2, 5])) {
   228→            // Cuando el plan actual está inactivo o vencido, se asigna el estado 'activo'
   229→            $request->merge(['estado_id' => 1]);
   230→        }
   231→
   232→        $this->verificarYAsignarTiempo($empresa_plan, $request);
   233→        $plan_renovado = $this->repositorio->crear($request->toArray());
   234→
   235→        if ($request->filled('pruebas')) {
   236→            $this->repositorio->sincronizarPruebas($plan_renovado, $request->pruebas);
   237→        }
   238→
   239→        if ($request->filled('funcionalidades')) {
   240→            $this->repositorio->sincronizarFuncionalidades($plan_renovado, $request->funcionalidades);
   241→        }
   242→
   243→        return $plan_renovado;
   244→    }
