   246→    /**
   247→     * Verificar si el plan es por tiempo y asignar el tiempo de vigencia en caso de serlo.
   248→     *
   249→     * @param EmpresaPlan $empresa_plan Plan de la empresa.
   250→     * @param Request $request Contenido de la petición.
   251→     * @return void
   252→     * @author Daniel Beltrán
   253→     */
   254→    public function verificarYAsignarTiempo(EmpresaPlan $empresa_plan, Request &$request): void {
   255→        if (!is_null($empresa_plan->fecha_inicio) && !is_null($empresa_plan->fecha_vencimiento)) {
   256→            $request->merge($this->calcularFechasVigencia($empresa_plan->plan->vigencia, $empresa_plan->fecha_vencimiento->addDay()));
   257→        }
   258→    }
   259→
   260→    /**
   261→     * Calcular las fechas en las que el plan estará vigente.
   262→     *
   263→     * @param int $vigencia Cantidad de días de vigencia.
   264→     * @param Carbon|null $fecha_inicio_vigencia Fecha en la que inicia la vigencia.
   265→     * @return array Fechas de inicio y vencimiento.
   266→     * @author Daniel Beltrán
   267→     */
   268→    public function calcularFechasVigencia(int $vigencia, ?Carbon $fecha_inicio_vigencia = null): array {
   269→        $hoy          = now();
   270→        $fecha_inicio = !is_null($fecha_inicio_vigencia) ? $fecha_inicio_vigencia : $hoy;
   271→
   272→        // Si es múltiplo de 3, entonces la vigencia es en meses
   273→        if (($vigencia % 3) === 0 && gettype($vigencia / 30) === 'integer') {
   274→            $meses             = $vigencia / 30;
   275→            $fecha_vencimiento = $fecha_inicio->copy()->addMonths($meses);
   276→
   277→            if ($fecha_inicio->day === 1) {
   278→                $fecha_vencimiento->subDay();
   279→            }
   280→        } else {
   281→            $vigencia          = $vigencia < 0 ? abs($vigencia) : $vigencia; // Convierte los valores negativos a positivos
   282→            $fecha_vencimiento = $fecha_inicio->copy()->addDays($vigencia);
   283→        }
   284→
   285→        return [
   286→            'fecha_inicio'      => $fecha_inicio->format('Y-m-d'),
   287→            'fecha_vencimiento' => $fecha_vencimiento->format('Y-m-d')
   288→        ];
   289→    }
