   478→     * @author Fahibram Cárcamo
   479→     */
   480→    public function verificarVigenciaPlan(EmpresaPlan $empresa_plan): bool {
   481→        if (!is_null($empresa_plan->fecha_vencimiento) && $empresa_plan->fecha_vencimiento->diffInDays(now()) < 0) {
   482→            return true;
   483→        } elseif (!is_null($empresa_plan->unidades) && $empresa_plan->unidades > 0) {
   484→            if (($empresa_plan->unidades - $empresa_plan->consumos_agendas) > 0) {
   485→                return true;
   486→            }
   487→        }
   488→
   489→        return false;
   490→    }
