    92→    public function getTodo(Request $request): Collection {
    93→        $con_consumos_agendas = $request->filled('con_consumos_agendas')
    94→            && filter_var($request->con_consumos_agendas, FILTER_VALIDATE_BOOLEAN);
    95→        $con_consumos_ofertas_laborales = $request->filled('con_consumos_ofertas_laborales')
    96→            && filter_var($request->con_consumos_ofertas_laborales, FILTER_VALIDATE_BOOLEAN);
    97→        $con_conteo_consumos_agendas = $request->filled('con_conteo_consumos_agendas')
    98→            && filter_var($request->con_conteo_consumos_agendas, FILTER_VALIDATE_BOOLEAN);
    99→        $con_conteo_consumos_ofertas_laborales = $request->filled('con_conteo_consumos_ofertas_laborales')
   100→            && filter_var($request->con_conteo_consumos_ofertas_laborales, FILTER_VALIDATE_BOOLEAN);
   101→
   102→        return $this->modelo::select('id', 'empresa_id', 'plan_id', 'plan_credito_id', 'estado_id', 'unidades', 'cantidad_ofertas_laborales', 'cantidad_usuarios', 'cantidad_pruebas', 'creditos', 'fecha_inicio', 'fecha_vencimiento', 'precio', 'porcentaje_descuento', 'numero_factura', 'comentario', 'plan_original', 'renovando', 'por_facturar', 'created_at')
   103→        ->with([
   104→            'plan:id,tipo_prueba_id,nombre,cantidad_pruebas,cantidad_usuarios,cantidad_ofertas_laborales,unidades,vigencia,precio',
   105→            'empresa:id,empresa_id,nombre_comercial,razon_social',
   106→            'plan.tipoPrueba:id,nombre',
   107→            'planCredito:id,nombre,creditos,precio,porcentaje_descuento,fecha_inicio_promocion,fecha_fin_promocion',
   108→            'estado:id,nombre',
   109→            'pruebas:id,nombre',
   110→            'funcionalidades:id,nombre,predeterminado',
   111→            'archivos' => function ($archivos) {
   112→                $archivos->select('id', 'modelo', 'modelo_id', 'nombre', 'ruta', 'created_at')->orderBy('created_at', 'DESC');
   113→            }
   114→        ])
   115→        ->when($con_consumos_agendas || $con_consumos_ofertas_laborales, function ($query) use ($con_consumos_agendas, $con_consumos_ofertas_laborales) {
   116→            $query->with([
   117→                'consumos' => function ($consumos) use ($con_consumos_agendas, $con_consumos_ofertas_laborales) {
   118→                    $consumos->select('id', 'empresa_plan_id', 'empresa_id', 'agenda_id', 'oferta_laboral_id')
   119→                        ->with(['empresa' => function ($empresa) {
   120→                            $empresa->select('id', 'empresa_id', 'nombre_comercial', 'razon_social')->with('usuarios:id,nombres,apellidos');
   121→                        }])
   122→                        ->when($con_consumos_agendas, fn($consulta) => $consulta->whereNotNull('agenda_id'))
   123→                        ->when($con_consumos_ofertas_laborales, fn($consulta) => $consulta->orWhereNotNull('oferta_laboral_id'));
   124→                },
   125→                'consumos.agenda:id,prueba_id',
   126→                'consumos.agenda.prueba:id,nombre,slug'
   127→            ]);
   128→        })
   129→        ->when($con_conteo_consumos_agendas, function ($consulta) {
   130→            $consulta->withCount(['consumos as conteo_consumos_agendas' => fn($consulta_consumos) => $consulta_consumos->whereNotNull('agenda_id')]);
   131→        })
   132→        ->when($con_conteo_consumos_ofertas_laborales, function ($consulta) {
   133→            $consulta->withCount(['consumos as conteo_consumos_ofertas_laborales' => fn($consulta_consumos) => $consulta_consumos->whereNotNull('oferta_laboral_id')]);
   134→        })
   135→        ->when($request->filled('empresa_id'), function ($query) use ($request) {
   136→            $query->where('empresa_id', $request->empresa_id);
   137→        })
   138→        ->when($request->filled('tipo_prueba_id'), function ($query) use ($request) {
   139→            $query->whereHas('plan', function ($plan) use ($request) {
   140→                $plan->where('tipo_prueba_id', $request->tipo_prueba_id);
   141→            });
   142→        })
   143→        ->when($request->filled('nombre_plan'), function ($query) use ($request) {
   144→            $query->whereHas('plan', function ($plan) use ($request) {
   145→                $plan->where('nombre', 'like', "%{$request->nombre_plan}%");
   146→            });
   147→        })
   148→        ->when($request->filled('plan_id'), function ($query) use ($request) {
   149→            $query->where('plan_id', $request->plan_id);
   150→        })
   151→        ->when(
   152→            $request->has('en') && filter_var($request->en, FILTER_VALIDATE_BOOLEAN) && $request->filled('campo') && $request->filled('valores'),
   153→            function ($query) use ($request) {
   154→                $query->whereIn($request->campo, $request->valores);
   155→            }
   156→        )
   157→        ->when($request->filled('estado_id'), function ($consulta) use ($request) {
   158→            $consulta->whereIn('estado_id', $request->estado_id);
   159→        })
   160→        ->when(
   161→            $request->filled('duracion') && $request->duracion === 'con_tiempo',
   162→            fn($consulta) => $consulta->whereNotNull('fecha_vencimiento')
   163→        )
   164→        ->when(
   165→            $request->filled('duracion') && $request->duracion === 'sin_tiempo',
   166→            fn($consulta) => $consulta->whereNull('fecha_vencimiento')
   167→        )
   168→        ->when(
   169→            $request->filled('fecha_vencimiento_desde'),
   170→            fn($consulta) => $consulta->where('fecha_vencimiento', '>=', $request->fecha_vencimiento_desde)
   171→        )
   172→        ->when(
   173→            $request->filled('fecha_vencimiento_hasta'),
   174→            fn($consulta) => $consulta->where('fecha_vencimiento', '<=', $request->fecha_vencimiento_hasta)
   175→        )
   176→        ->when(
   177→            $request->filled('fecha_inicio_desde'),
   178→            fn($consulta) => $consulta->where('fecha_inicio', '>=', $request->fecha_inicio_desde)
   179→        )
   180→        ->when(
   181→            $request->filled('fecha_inicio_hasta'),
   182→            fn($consulta) => $consulta->where('fecha_inicio', '<=', $request->fecha_inicio_hasta)
   183→        )
   184→        ->orderBy('created_at', 'DESC')
   185→        ->get();
   186→    }
