   530→    /**
   531→     * Obtener los registros de la tabla empresas_planes con sus datos de consumo de todas las empresas.
   532→     *
   533→     * @return Collection
   534→     * @author Fahibram Cárcamo
   535→     * @author Andres Gonzalez
   536→     * @author José Manuel Jiménez
   537→     * @author Jair Carvajal
   538→     * @author Daniel Beltrán
   539→     */
   540→    public function datosConsumosEmpresas(Request $request): Collection {
   541→        $roles_tipo_empresa_ids = [
   542→            RolRepository::ROL_PROPIETARIO_ID,
   543→            RolRepository::ROL_ADMINISTRADOR_GENERAL_ID,
   544→            RolRepository::ROL_ADMINISTRADOR_SUCURSAL_ID,
   545→            RolRepository::ROL_EVALUADOR_ID
   546→        ];
   547→
   548→        $matriz_conteos = [
   549→            'consumos as conteo_consumos_agendas'           => fn($consulta_consumos) => $consulta_consumos->whereNotNull('agenda_id'),
   550→            'consumos as conteo_consumos_ofertas_laborales' => fn($consulta_consumos) => $consulta_consumos->whereNotNull('oferta_laboral_id'),
   551→            'usuarios as conteo_usuarios'                   => fn($consulta_usuarios) => $consulta_usuarios->whereHas(
   552→                'empresasUsuarios',
   553→                fn($consulta_empresas_usuarios) => $consulta_empresas_usuarios->whereIn('rol_id', $roles_tipo_empresa_ids)
   554→            )
   555→        ];
   556→
   557→        $campos_empresas = ['id', 'empresa_id', 'nombre_comercial', 'razon_social', 'nombre_contacto', 'celular_contacto', 'email_contacto', 'telefono_contacto', 'extension_contacto', 'nombre_tesorero', 'celular_tesorero', 'telefono_tesorero', 'email_tesorero'];
   558→
   559→        return $this->modelo::select('id', 'empresa_id', 'plan_id', 'plan_credito_id', 'usuario_id', 'estado_id', 'unidades', 'cantidad_ofertas_laborales', 'cantidad_usuarios', 'cantidad_pruebas', 'creditos', 'fecha_inicio', 'fecha_vencimiento', 'precio', 'porcentaje_descuento', 'numero_factura', 'comentario', 'plan_original', 'renovando', 'por_facturar', 'created_at')
   560→            ->addSelect([
   561→                DB::raw('
   562→                    (
   563→                        SELECT COUNT(DISTINCT eu.usuario_id)
   564→                        FROM empresas_usuarios AS eu
   565→                        LEFT JOIN empresas AS suc ON eu.empresa_id = suc.id
   566→                        WHERE (eu.empresa_id = empresas_planes.empresa_id OR suc.empresa_id = empresas_planes.empresa_id)
   567→                        AND eu.rol_id IN (' . implode(',', $roles_tipo_empresa_ids) . ')
   568→                    ) AS conteo_total_usuarios_empresa_sucursales
   569→                '),
   570→                DB::raw('
   571→                    (
   572→                        SELECT COUNT(*)
   573→                        FROM consumos
   574→                        LEFT JOIN empresas AS suc ON consumos.empresa_id = suc.id
   575→                        WHERE (
   576→                            consumos.empresa_id = empresas_planes.empresa_id
   577→                            OR suc.empresa_id = empresas_planes.empresa_id
   578→                        )
   579→                        AND consumos.agenda_id IS NOT NULL
   580→                    ) AS conteo_total_consumos_agendas_empresa_sucursales
   581→                '),
   582→                DB::raw('
   583→                    (
   584→                        SELECT COUNT(*)
   585→                        FROM consumos
   586→                        LEFT JOIN empresas AS suc ON consumos.empresa_id = suc.id
   587→                        WHERE (
   588→                            consumos.empresa_id = empresas_planes.empresa_id
   589→                            OR suc.empresa_id = empresas_planes.empresa_id
   590→                        )
   591→                        AND consumos.oferta_laboral_id IS NOT NULL
   592→                    ) AS conteo_total_consumos_ofertas_laborales_empresa_sucursales
   593→                '),
   594→            ])
   595→            ->with([
   596→                'plan:id,tipo_prueba_id,nombre,unidades,cantidad_ofertas_laborales,vigencia,precio',
   597→                'plan.tipoPrueba:id,nombre',
   598→                'pruebas:id,nombre,slug',
   599→                'estado:id,nombre',
   600→                'usuario:id,nombres,apellidos',
   601→                'empresa' => function ($consulta_empresa) use ($matriz_conteos, $campos_empresas) {
   602→                    $consulta_empresa->select($campos_empresas)
   603→                        ->whereNull('empresa_id')
   604→                        ->withCount([
   605→                            ...$matriz_conteos,
   606→                            'sucursales as conteo_sucursales'
   607→                        ])
   608→                        ->with([
   609→                            'sucursales' => function ($consulta_sucursal) use ($matriz_conteos, $campos_empresas) {
   610→                                $consulta_sucursal->select($campos_empresas)->withCount($matriz_conteos);
   611→                            }
   612→                        ]);
   613→                }
   614→            ])
   615→            ->when(
   616→                $request->filled('estado_id') && !in_array($request->estado_id, [3, 8]),
   617→                fn($consulta_estado) => $consulta_estado->where('estado_id', $request->estado_id)
   618→            )
   619→            ->whereNotIn('estado_id', [3, 8])
   620→            ->when(
   621→                $request->filled('prueba_id'),
   622→                fn($consulta) => $consulta->whereHas(
   623→                    'pruebas',
   624→                    fn($consulta_prueba) => $consulta_prueba->where('pruebas.id', $request->prueba_id)
   625→                )
   626→            )
   627→            ->when($request->filled('ejecutivo_id'), function ($consulta) use ($request) {
   628→                $consulta->whereHas('empresa', fn($empresa) => $empresa->where('ejecutivo_id', $request->ejecutivo_id));
   629→            })
   630→            ->when($request->filled('plan_por'), function ($consulta_plan_por) use ($request) {
   631→                switch ($request->plan_por) {
   632→                    case 'por unidades':
   633→                        $consulta_plan_por->whereNotNull('unidades');
   634→                        break;
   635→
   636→                    case 'por ofertas laborales':
   637→                        $consulta_plan_por->whereNotNull('cantidad_ofertas_laborales');
   638→                        break;
   639→
   640→                    case 'por tiempo':
   641→                        $consulta_plan_por->whereNotNull('fecha_inicio')->whereNotNull('fecha_vencimiento');
   642→                        break;
   643→                }
   644→            })
   645→            ->when($request->filled('fecha_vencimiento'), function ($consulta, $buscar) use ($request) {
   646→                $fecha_inicio = $request->fecha_vencimiento;
   647→                $fecha_fin    = $request->fecha_vencimiento;
   648→
   649→                if (strpos($request->fecha_vencimiento, ' a ') !== false) {
   650→                    $fechas       = explode(' a ', $request->fecha_vencimiento);
   651→                    $fecha_inicio = $fechas[0];
   652→                    $fecha_fin    = $fechas[1];
   653→                }
   654→
   655→                $consulta->whereBetween(DB::raw('DATE(fecha_vencimiento)'), [$fecha_inicio, $fecha_fin]);
   656→            })
   657→            ->when($request->filled('fecha_inicio'), function ($consulta, $buscar) use ($request) {
   658→                $fecha_inicio = $request->fecha_inicio;
   659→                $fecha_fin    = $request->fecha_inicio;
   660→
   661→                if (strpos($request->fecha_inicio, ' a ') !== false) {
   662→                    $fechas       = explode(' a ', $request->fecha_inicio);
   663→                    $fecha_inicio = $fechas[0];
   664→                    $fecha_fin    = $fechas[1];
   665→                }
   666→
   667→                $consulta->whereBetween(DB::raw('DATE(fecha_inicio)'), [$fecha_inicio, $fecha_fin]);
   668→            })
   669→            ->when(
   670→                $request->has('empresas_planes_ids') && is_array($request->empresas_planes_ids) && count($request->empresas_planes_ids) > 0,
   671→                fn($consulta_empresas_planes) => $consulta_empresas_planes->whereIn('id', $request->empresas_planes_ids)
   672→            )
   673→            ->when($request->filled('tipo_activacion'), function ($consulta_tipo_activacion) use ($request) {
   674→                if ($request->tipo_activacion === 'compra') {
   675→                    $consulta_tipo_activacion->whereNotExists(function ($subconsulta) {
   676→                        $subconsulta->select(DB::raw(1))
   677→                            ->from('empresas_planes AS empresas_planes_subconsulta')
   678→                            ->whereColumn('empresas_planes_subconsulta.plan_id', 'empresas_planes.plan_id')
   679→                            ->whereColumn('empresas_planes_subconsulta.empresa_id', 'empresas_planes.empresa_id')
   680→                            ->whereColumn('empresas_planes_subconsulta.id', '<>', 'empresas_planes.id');
   681→                    });
   682→                } elseif ($request->tipo_activacion === 'recompra') {
   683→                    $consulta_tipo_activacion->whereExists(function ($subconsulta) {
   684→                        $subconsulta->select(DB::raw(1))
   685→                            ->from('empresas_planes AS empresas_planes_subconsulta')
   686→                            ->whereColumn('empresas_planes_subconsulta.plan_id', 'empresas_planes.plan_id')
   687→                            ->whereColumn('empresas_planes_subconsulta.empresa_id', 'empresas_planes.empresa_id')
   688→                            ->whereColumn('empresas_planes_subconsulta.id', '<>', 'empresas_planes.id');
   689→                    });
   690→                }
   691→            })
   692→            ->get();
   693→    }
