   775→                )
   776→                AS conteo_sucursales
   777→            '),
   778→            DB::raw('
   779→                (
   780→                    SELECT CONCAT(COALESCE(GROUP_CONCAT(DISTINCT sucursales.nombre_comercial SEPARATOR ","), ""))
   781→                    FROM empresas AS sucursales
   782→                    WHERE sucursales.empresa_id = empresas_planes.empresa_id
   783→                )
   784→                AS nombres_comerciales_sucursales
   785→            ')
   786→        )
   787→        ->leftJoin('planes', 'empresas_planes.plan_id', '=', 'planes.id')
   788→        ->leftJoin('tipos_pruebas', 'planes.tipo_prueba_id', '=', 'tipos_pruebas.id')
   789→        ->leftJoin('estados_planes', 'empresas_planes.estado_id', '=', 'estados_planes.id')
   790→        ->leftJoin('users', 'empresas_planes.usuario_id', '=', 'users.id')
   791→        ->leftJoin('empresas', 'empresas_planes.empresa_id', '=', 'empresas.id')
   792→        ->whereNull('empresas.empresa_id')
   793→        ->when(
   794→            $request->filled('plan_por'),
   795→            function ($consulta) use ($request) {
   796→                switch ($request->plan_por) {
   797→                    case 'por unidades':
   798→                        $consulta->whereNotNull('empresas_planes.unidades');
   799→                        break;
   800→
   801→                    case 'por ofertas laborales':
   802→                        $consulta->whereNotNull('empresas_planes.cantidad_ofertas_laborales');
   803→                        break;
   804→
   805→                    case 'por tiempo':
   806→                        $consulta->whereNotNull('empresas_planes.fecha_inicio')->whereNotNull('empresas_planes.fecha_vencimiento');
   807→                        break;
   808→                }
   809→            }
   810→        )
   811→        ->when(
   812→            $request->filled('tipo_prueba_id'),
   813→            fn($consulta) => $consulta->where('planes.tipo_prueba_id', $request->tipo_prueba_id),
   814→            fn($consulta) => $consulta->whereIn('planes.tipo_prueba_id', [
   815→                TipoPrueba::SELECCION_ID,
   816→                TipoPrueba::ORGANIZACIONAL_ID,
   817→                TipoPrueba::VOCACIONAL_ID
   818→            ])
   819→        )
   820→        ->when($request->filled('estado_id'), fn($consulta) => $consulta->where('empresas_planes.estado_id', $request->estado_id))
   821→        ->when(
   822→            $request->filled('ejecutivo_id'),
   823→            function ($consulta) use ($request) {
   824→                $consulta->whereExists(function ($subconsulta) use ($request) {
   825→                    $subconsulta->select(DB::raw(1))
   826→                        ->from('empresas_usuarios')
   827→                        ->whereColumn('empresas_usuarios.empresa_id', 'empresas_planes.empresa_id')
   828→                        ->where('empresas_usuarios.usuario_id', $request->ejecutivo_id)
   829→                        ->where('empresas_usuarios.rol_id', RolRepository::ROL_EJECUTIVO_CUENTA_ID);
   830→                });
   831→            }
   832→        )
   833→        ->when(
   834→            $request->filled('fecha_vencimiento'),
   835→            fn($consulta) => $consulta->where('empresas_planes.fecha_vencimiento', '<=', $request->fecha_vencimiento)
   836→        )
   837→        ->when(
   838→            $request->filled('fecha_inicio'),
   839→            fn($consulta) => $consulta->where('empresas_planes.fecha_inicio', '>=', $request->fecha_inicio)
   840→        )
   841→        ->when(
