   848→    /**
   849→     * Obtener el historial de compras de planes con cr鑠ditos de una empresa.
   850→     *
   851→     * @param int $empresa_id ID de la empresa.
   852→     * @param Request $request Contenido de la peticion.
   853→     * @return Collection
   854→     * @author Sergio Vargas
   855→     */
   856→    public function historialComprasCreditos(int $empresa_id, Request $request): Collection {
   857→        return $this->modelo::select(
   858→            'id',
   859→            'empresa_id',
   860→            'plan_credito_id',
   861→            'estado_id',
   862→            'creditos',
   863→            'fecha_inicio',
   864→            'fecha_vencimiento',
   865→            'precio',
   866→            'porcentaje_descuento',
   867→            'numero_factura',
   868→            'comentario',
   869→            'created_at'
   870→        )
   871→        ->with([
   872→            'planCredito:id,nombre,creditos,precio,porcentaje_descuento,fecha_inicio_promocion,fecha_fin_promocion',
   873→            'estado:id,nombre',
   874→            'archivos' => function ($archivos) {
   875→                $archivos->select('id', 'modelo', 'modelo_id', 'nombre', 'ruta', 'created_at')
   876→                    ->orderBy('created_at', 'DESC');
   877→            }
   878→        ])
   879→        ->withSum('consumosCreditos as creditos_consumidos', 'creditos')
   880→        ->where('empresa_id', $empresa_id)
   881→        ->whereNotNull('plan_credito_id')
   882→        ->when($request->filled('estado_id'), fn($consulta) => $consulta->where('estado_id', $request->estado_id))
   883→        ->when(
   884→            $request->filled('duracion') && $request->duracion === 'con_tiempo',
   885→            fn($consulta) => $consulta->whereNotNull('fecha_vencimiento')
   886→        )
   887→        ->when(
   888→            $request->filled('duracion') && $request->duracion === 'sin_tiempo',
   889→            fn($consulta) => $consulta->whereNull('fecha_vencimiento')
   890→        )
   891→        ->when(
   892→            $request->filled('fecha_vencimiento_desde'),
   893→            fn($consulta) => $consulta->where('fecha_vencimiento', '>=', $request->fecha_vencimiento_desde)
   894→        )
   895→        ->when(
   896→            $request->filled('fecha_vencimiento_hasta'),
   897→            fn($consulta) => $consulta->where('fecha_vencimiento', '<=', $request->fecha_vencimiento_hasta)
   898→        )
   899→        ->when(
   900→            $request->filled('fecha_inicio_desde'),
   901→            fn($consulta) => $consulta->where('fecha_inicio', '>=', $request->fecha_inicio_desde)
   902→        )
   903→        ->when(
   904→            $request->filled('fecha_inicio_hasta'),
   905→            fn($consulta) => $consulta->where('fecha_inicio', '<=', $request->fecha_inicio_hasta)
   906→        )
   907→        ->orderBy('created_at', 'DESC')
   908→        ->get();
   909→    }
   910→}
